@import url('https://fonts.googleapis.com/css2?family=Inter:wght@300;400;500;600;700&display=swap');
@import url('https://fonts.googleapis.com/css2?family=Poppins:wght@400;500;600;700&display=swap');
@tailwind base;
@tailwind components;
@tailwind utilities;

/* ========== Design Tokens (HSL) ========== */
@layer base {
  :root {
    --background: 0 0% 100%;
    --foreground: 222.2 84% 4.9%;
    --card: 0 0% 100%;
    --card-foreground: 222.2 84% 4.9%;
    --primary: 221 83% 53%;
    --primary-foreground: 210 40% 98%;
    --secondary: 210 40% 96.1%;
    --secondary-foreground: 222.2 47.4% 11.2%;
    --muted: 210 40% 96.1%;
    --muted-foreground: 215.4 16.3% 46.9%;
    --accent: 210 40% 96.1%;
    --accent-foreground: 222.2 47.4% 11.2%;
    --destructive: 0 84.2% 60.2%;
    --destructive-foreground: 210 40% 98%;
    --border: 214.3 31.8% 91.4%;
    --ring: 222.2 84% 4.9%;
    --radius: 0.5rem;

    /* Extended brand tokens */
    --primary-glow: 221 100% 63%;
    --gradient-primary: linear-gradient(135deg, hsl(var(--primary)), hsl(var(--primary-glow)));
    --shadow-elevated: 0 10px 30px -10px hsl(var(--primary) / 0.35);
  }

  .dark {
    --background: 222.2 84% 4.9%;
    --foreground: 210 40% 98%;
    --card: 222.2 84% 4.9%;
    --card-foreground: 210 40% 98%;
    --primary: 210 40% 98%;
    --primary-foreground: 222.2 47.4% 11.2%;
    --secondary: 217.2 32.6% 17.5%;
    --secondary-foreground: 210 40% 98%;
    --muted: 217.2 32.6% 17.5%;
    --muted-foreground: 215 20.2% 65.1%;
    --accent: 217.2 32.6% 17.5%;
    --accent-foreground: 210 40% 98%;
    --destructive: 0 62.8% 30.6%;
    --destructive-foreground: 210 40% 98%;
    --border: 217.2 32.6% 17.5%;
    --ring: 212.7 26.8% 83.9%;
  }
}

/* Custom component styles */
@layer components {
  .hero {
    position: relative;
    min-height: 68vh;
    display: grid;
    align-items: center;
  }

  .hero::before {
    content: "";
    position: absolute;
    inset: 0;
    background: linear-gradient(180deg, hsl(var(--background) / 0.25), hsl(var(--background)));
  }

  .card {
    @apply bg-white border border-gray-200 rounded-2xl p-6 transition-all duration-200;
  }

  .card:hover {
    @apply shadow-lg transform scale-105;
  }

  .cat {
    @apply bg-white border border-gray-200 rounded-2xl p-6 transition-all duration-200 cursor-pointer;
  }

  .cat:hover {
    @apply shadow-lg transform scale-105;
  }

  .story-link {
    @apply relative inline-block;
  }

  .story-link::after {
    content: "";
    @apply absolute left-0 bottom-0 w-full h-0.5 bg-primary transform scale-x-0 origin-bottom-right transition-transform duration-300;
  }

  .story-link:hover::after {
    @apply scale-x-100 origin-bottom-left;
  }

  /* Animation utilities */
  .hover-scale {
    @apply transition-transform duration-200;
  }

  .hover-scale:hover {
    @apply transform scale-105;
  }

  /* Trust bar styling */
  .trust-bar {
    @apply bg-secondary/20 border-t border-b border-gray-200;
  }

  /* Enhanced shadow for elevated elements */
  .shadow-elevated {
    box-shadow: var(--shadow-elevated);
  }
}

/* Apply Poppins font */
body {
  font-family: "Poppins", ui-sans-serif, system-ui, -apple-system, "Segoe UI", Roboto, "Helvetica Neue", Arial, sans-serif;
}

:root {
  --background: hsl(0 0% 100%);
  --foreground: hsl(210 25% 7.8431%);
  --card: hsl(180 6.6667% 97.0588%);
  --card-foreground: hsl(210 25% 7.8431%);
  --popover: hsl(0 0% 100%);
  --popover-foreground: hsl(210 25% 7.8431%);
  --primary: hsl(213 100% 50%);
  --primary-foreground: hsl(0 0% 100%);
  --secondary: hsl(142 76% 36%);
  --secondary-foreground: hsl(0 0% 100%);
  --muted: hsl(240 1.9608% 90%);
  --muted-foreground: hsl(210 25% 7.8431%);
  --accent: hsl(0 84.2% 60.2%);
  --accent-foreground: hsl(0 0% 100%);
  --destructive: hsl(0 84.2% 60.2%);
  --destructive-foreground: hsl(0 0% 100%);
  --border: hsl(201.4286 30.4348% 90.9804%);
  --input: hsl(200 23.0769% 97.4510%);
  --ring: hsl(213.3 88% 53.1%);
  --chart-1: hsl(213.3 88% 53.1%);
  --chart-2: hsl(159.7826 100% 36.0784%);
