import { useState, useEffect } from "react";
import { useLocation } from "wouter";
import Header from "@/components/layout/header";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Tabs, TabsContent, TabsList, TabsTrigger } from "@/components/ui/tabs";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { Badge } from "@/components/ui/badge";
import { useToast } from "@/hooks/use-toast";
import { 
  Settings, 
  Package, 
  FileText, 
  Users, 
  Edit, 
  Save, 
  Trash2, 
  Plus,
  Upload,
  Eye,
  CheckCircle,
  XCircle,
  Clock,
  Activity,
  AlertCircle,
  User,
  Search,
  Filter
} from "lucide-react";
import { Dialog, DialogContent, DialogHeader, DialogTitle, DialogTrigger } from "@/components/ui/dialog";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { Table, TableBody, TableCell, TableHead, TableHeader, TableRow } from "@/components/ui/table";
import { useProducts } from "@/contexts/ProductContext";
import * as XLSX from 'xlsx';

interface AdminUser {
  id: string;
  username?: string;
  email: string;
  firstName?: string;
  lastName?: string;
  isAdmin: boolean;
  adminRole?: string;
  adminPermissions?: { permission: string }[];
}

interface Product {
  id?: string;
  Category?: string;
  'Product Name'?: string;
  name?: string;
  Brand?: string;
  brand?: { name: string };
  category?: { name: string };
  Price?: number;
  price?: string;
  ImageURL?: string;
  imageUrl?: string;
}


interface LogEntry {
  id: string;
  action: string;
  details: string;
  timestamp: string;
  user?: string;
}



export default function AdminPage() {
  const [, setLocation] = useLocation();
  const [currentUser, setCurrentUser] = useState<AdminUser | null>(null);
  const [editingProduct, setEditingProduct] = useState<Product | null>(null);
  const [isAddingProduct, setIsAddingProduct] = useState(false);
  const [isEditDialogOpen, setIsEditDialogOpen] = useState(false);
  const [logs, setLogs] = useState<LogEntry[]>([]);
  const [loading, setLoading] = useState(true);

  // Use ProductContext for shared product management  
  const { products, updateProduct, deleteProduct, addProduct, customCategories, addCategory, updateCategory, deleteCategory, categories: allCategories } = useProducts();

  // Add missing state variables that were removed
  const [searchQuery, setSearchQuery] = useState('');
  const [selectedCategory, setSelectedCategory] = useState('All');
  const productsLoading = false; // Since we're using context now

  // Category Management state
  const [isManagingCategories, setIsManagingCategories] = useState(false);
  const [newCategoryName, setNewCategoryName] = useState('');
  const [editingCategoryOld, setEditingCategoryOld] = useState('');
  const [editingCategoryNew, setEditingCategoryNew] = useState('');

  // Use categories from context instead of local calculation
  const categories: string[] = allCategories || [];

  // Filter products based on search and category
  const filteredProducts = products.filter((product: any) => {
    const matchesSearch = !searchQuery || 
      (product['Product Name'] || product.name || '').toLowerCase().includes(searchQuery.toLowerCase()) ||
      (product.Brand || product.brand?.name || '').toLowerCase().includes(searchQuery.toLowerCase()) ||
      (product.Category || product.category?.name || '').toLowerCase().includes(searchQuery.toLowerCase());

    const matchesCategory = selectedCategory === 'All' || 
      (product.Category === selectedCategory) || 
      (product.category?.name === selectedCategory);

    return matchesSearch && matchesCategory;
  });

  const { toast } = useToast();



  // Add log entry
  const addLog = (action: string, details: string) => {
    const newLog: LogEntry = {
      id: Date.now().toString(),
      action,
      details,
      timestamp: new Date().toLocaleString(),
      user: currentUser?.username || currentUser?.email || 'Admin'
    };
    setLogs(prev => [newLog, ...prev.slice(0, 99)]); // Keep last 100 logs
  };


  // Check admin authentication
  useEffect(() => {
    const checkAuth = async () => {
      try {
        // First check localStorage for role-based admin login
        const userRole = localStorage.getItem('role');
        const adminUsername = localStorage.getItem('adminUsername');

        if (userRole === 'admin' && adminUsername === 'Admin1') {
          // Use hardcoded admin credentials - separate from regular users
          setCurrentUser({
            id: 'admin-hardcoded-001',
            username: adminUsername,
            email: 'admin@smilepills.com',
            isAdmin: true,
            adminRole: 'super_admin',
            firstName: 'Admin',
            lastName: '1'
          });
          setLoading(false);
          return;
        }

        // Fallback to backend authentication
        const response = await fetch('/api/auth/user', {
          credentials: 'include'
        });

        if (response.ok) {
          const data = await response.json();
          if (data.user && data.user.isAdmin) {
            setCurrentUser(data.user);
          } else {
            setLocation('/admin/login');
          }
        } else {
          setLocation('/admin/login');
        }
      } catch (error) {
        console.error('Auth check failed:', error);
        setLocation('/admin/login');
      }
      setLoading(false);
    };

    checkAuth();
  }, [setLocation]);

  const handleEditProduct = (product: Product) => {
    setEditingProduct({ ...product });
    setIsEditDialogOpen(true);
    addLog('Product Edit', `Started editing product: ${product.name || product['Product Name']}`);
  };

  const handleSaveProduct = async (updatedProductData?: any) => {
    const productToSave = updatedProductData || editingProduct;
    if (!productToSave || !productToSave.id) return;

    try {
      // Convert the product to proper format for the update
      const updates: any = {
        name: productToSave.name,
        'Product Name': productToSave['Product Name'],
        Category: productToSave.Category,
        Brand: productToSave.Brand,
        Price: productToSave.Price,
        price: productToSave.price,
        ImageURL: productToSave.ImageURL,
        imageUrl: productToSave.imageUrl,
      };

      console.log('Admin: Calling updateProduct for ID:', productToSave.id, 'with updates:', updates);
      updateProduct(productToSave.id, updates);
      setEditingProduct(null);
      setIsEditDialogOpen(false);
      console.log('Admin: Product update completed');
      addLog('Product Updated', `${productToSave.name || productToSave['Product Name']} was modified`);
      toast({
        title: "Product Updated",
        description: "Product has been updated successfully",
      });
    } catch (error) {
      console.error('Error updating product:', error);
      toast({
        title: "Error",
        description: "Failed to update product",
        variant: "destructive",
      });
    }
  };

  const handleDeleteProduct = async (productId: string | undefined, productName: string) => {
    if (!productId) return;

    try {
      console.log('Admin: Calling deleteProduct for ID:', productId);
      deleteProduct(productId);
      console.log('Admin: Product delete completed');
      addLog('Product Deleted', `${productName} was removed from catalog`);
      toast({
        title: "Product Deleted",
        description: "Product has been deleted successfully",
      });
    } catch (error) {
      console.error('Error deleting product:', error);
      toast({
        title: "Error",
        description: "Failed to delete product",
        variant: "destructive",
      });
    }
  };

  const handleAddProduct = async (newProduct: Omit<Product, 'id'>) => {
    try {
      // Create a clean product object for adding new product
      const cleanProduct: any = {
        'Product Name': newProduct['Product Name'],
        Category: newProduct.Category,
        Brand: newProduct.Brand,
        Price: newProduct.Price,
        ImageURL: newProduct.ImageURL,
      };
      await addProduct(cleanProduct);
      setIsAddingProduct(false);
      addLog('Product Added', `${newProduct.name || newProduct['Product Name']} was added to catalog`);
      toast({
        title: "Product Added",
        description: "Product has been added successfully",
      });
    } catch (error) {
      console.error('Error adding product:', error);
      toast({
        title: "Error",
        description: "Failed to add product",
        variant: "destructive",
      });
    }
  };

  if (loading || productsLoading) {
    return (
      <div className="min-h-screen bg-gray-50">
        <Header />
        <div className="container mx-auto px-4 py-8">
          <div className="flex items-center justify-center h-64">
            <div className="text-center">
              <Activity className="h-8 w-8 animate-spin mx-auto mb-4" />
              <p>Loading admin dashboard...</p>
            </div>
          </div>
        </div>
      </div>
    );
  }

  if (!currentUser) {
    return null; // Will redirect to login
  }

  return (
    <div className="min-h-screen bg-gray-50">
      <Header />

      <div className="container mx-auto px-4 py-8">
        <div className="mb-8">
          <h1 className="text-3xl font-bold text-gray-900">Admin Dashboard</h1>
          <p className="text-gray-600 mt-2">
            Welcome back, {currentUser.username || currentUser.email}
          </p>
        </div>

        <Tabs defaultValue="products" className="space-y-6">
          <TabsList className="grid w-full grid-cols-2">
            <TabsTrigger value="products" className="flex items-center gap-2">
              <Package className="h-4 w-4" />
              Product Management
            </TabsTrigger>
            <TabsTrigger value="logs" className="flex items-center gap-2">
              <FileText className="h-4 w-4" />
              Live Logs
            </TabsTrigger>
          </TabsList>

          <TabsContent value="products" className="space-y-6">
            <div className="flex justify-between items-center">
              <div>
                <h2 className="text-2xl font-semibold">Product Catalog</h2>
                <p className="text-gray-600">Manage your product inventory ({products.length} products shown)</p>
              </div>

              <Dialog open={isAddingProduct} onOpenChange={setIsAddingProduct}>
                <DialogTrigger asChild>
                  <Button 
                    className="flex items-center gap-2 bg-primary hover:bg-primary/90 shadow-lg" 
                    data-testid="button-add-product"
                    size="lg"
                  >
                    <Plus className="h-5 w-5" />
                    Add New Product
                  </Button>
                </DialogTrigger>
                <DialogContent className="max-w-lg">
                  <DialogHeader>
                    <DialogTitle className="flex items-center gap-2">
                      <Plus className="h-5 w-5" />
                      Add New Product to Catalog
                    </DialogTitle>
                  </DialogHeader>
                  <AddProductForm 
                    categories={categories}
                    onAdd={handleAddProduct}
                    onCancel={() => setIsAddingProduct(false)}
                    onAddCategory={(newCategory) => {
                      // Use the context function to add the category
                      addCategory(newCategory);
                    }}
                  />
                </DialogContent>
              </Dialog>
            </div>

            {/* Search and Filter Section */}
            <Card>
              <CardHeader>
                <CardTitle className="flex items-center gap-2">
                  <Filter className="h-5 w-5" />
                  Search & Filter Products
                </CardTitle>
              </CardHeader>
              <CardContent>
                <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                  <div className="relative">
                    <Search className="absolute left-3 top-1/2 transform -translate-y-1/2 text-gray-400 h-4 w-4" />
                    <Input
                      type="text"
                      placeholder="Search products by name, brand, or category..."
                      value={searchQuery}
                      onChange={(e) => setSearchQuery(e.target.value)}
                      className="pl-10"
                      data-testid="input-product-search"
                    />
                  </div>
                  <Select value={selectedCategory} onValueChange={setSelectedCategory}>
                    <SelectTrigger data-testid="select-product-category">
                      <SelectValue placeholder="Filter by category" />
                    </SelectTrigger>
                    <SelectContent>
                      <SelectItem value="All">All Categories ({products.length})</SelectItem>
                      {categories.map((category: string) => {
                        const count = products.filter((p: any) => (p.category?.name || p.Category) === category).length;
                        return (
                          <SelectItem key={category} value={category}>
                            {category} ({count})
                          </SelectItem>
                        );
                      })}
                    </SelectContent>
                  </Select>
                </div>
                {(searchQuery || selectedCategory !== 'All') && (
                  <div className="mt-4 flex items-center gap-2">
                    <span className="text-sm text-gray-600">Active filters:</span>
                    {searchQuery && (
                      <Badge variant="secondary" className="flex items-center gap-1">
                        Search: "{searchQuery}"
                        <button 
                          onClick={() => setSearchQuery('')}
                          className="ml-1 hover:bg-gray-300 rounded-full p-0.5"
                        >
                          ×
                        </button>
                      </Badge>
                    )}
                    {selectedCategory !== 'All' && (
                      <Badge variant="secondary" className="flex items-center gap-1">
                        Category: {selectedCategory}
                        <button 
                          onClick={() => setSelectedCategory('All')}
                          className="ml-1 hover:bg-gray-300 rounded-full p-0.5"
                        >
                          ×
                        </button>
                      </Badge>
                    )}
                    <Button
                      variant="outline"
                      size="sm"
                      onClick={() => {
                        setSearchQuery('');
                        setSelectedCategory('All');
                      }}
                      className="h-6 px-2 text-xs"
                    >
                      Clear All
                    </Button>
                  </div>
                )}
              </CardContent>
            </Card>

            {/* Category Management Section */}
            <Card>
              <CardHeader>
                <CardTitle className="flex items-center justify-between">
                  <div className="flex items-center gap-2">
                    <Settings className="h-5 w-5" />
                    Category Management
                  </div>
                  <Button
                    variant={isManagingCategories ? "outline" : "default"}
                    size="sm"
                    onClick={() => setIsManagingCategories(!isManagingCategories)}
                    data-testid="button-toggle-category-management"
                  >
                    {isManagingCategories ? 'Hide' : 'Show'}
                  </Button>
                </CardTitle>
              </CardHeader>
              {isManagingCategories && (
                <CardContent>
                  <div className="space-y-4">
                    {/* Add New Category */}
                    <div>
                      <Label htmlFor="newCategory">Add New Category</Label>
                      <div className="flex gap-2">
                        <Input
                          id="newCategory"
                          value={newCategoryName}
                          onChange={(e) => setNewCategoryName(e.target.value)}
                          placeholder="Enter category name"
                          data-testid="input-add-category"
                        />
                        <Button
                          type="button"
                          onClick={() => {
                            if (newCategoryName.trim()) {
                              // Use the context function to add the category (same as working Add Product form)
                              addCategory(newCategoryName.trim());
                              setNewCategoryName('');
                              addLog('Category Added', `New category: ${newCategoryName.trim()}`);
                              toast({
                                title: "Category Added",
                                description: `Category '${newCategoryName.trim()}' has been added successfully!`,
                              });
                            }
                          }}
                          disabled={!newCategoryName.trim()}
                          data-testid="button-add-category"
                        >
                          <Plus className="h-4 w-4 mr-2" />
                          Add
                        </Button>
                      </div>
                    </div>

                    {/* Existing Categories List */}
                    <div>
                      <Label>Manage Existing Categories ({categories.length})</Label>
                      <div className="space-y-2 max-h-48 overflow-y-auto">
                        {categories.map((category) => {
                          const productCount = products.filter((p: any) => 
                            (p.Category || p.category?.name) === category
                          ).length;
                          const isEditing = editingCategoryOld === category;

                          return (
                            <div key={category} className="flex items-center justify-between p-3 border rounded-lg bg-gray-50">
                              <div className="flex items-center gap-3">
                                <Badge variant="secondary">{category}</Badge>
                              </div>
                              <div className="flex items-center gap-2">
                                {isEditing ? (
                                  <>
                                    <Input
                                      value={editingCategoryNew}
