                {editingProduct && (
                  <EditProductForm
                    product={editingProduct}
                    categories={categories}
                    onSave={(updatedProduct) => {
                      handleSaveProduct();
                      setIsEditDialogOpen(false);
                    }}
                    onCancel={() => {
                      setIsEditDialogOpen(false);
                      setEditingProduct(null);
                    }}
