import { useState } from 'react';
import { useLocation } from 'wouter';
import { Button } from '@/components/ui/button';
import { Input } from '@/components/ui/input';
import { Label } from '@/components/ui/label';
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from '@/components/ui/card';
import { Tabs, TabsContent, TabsList, TabsTrigger } from '@/components/ui/tabs';
import { Alert, AlertDescription } from '@/components/ui/alert';
import { Eye, EyeOff, Lock, User } from 'lucide-react';
import { useForm } from 'react-hook-form';
import { zodResolver } from '@hookform/resolvers/zod';
import { z } from 'zod';
import { Form, FormControl, FormField, FormItem, FormLabel, FormMessage } from '@/components/ui/form';
import { useToast } from '@/hooks/use-toast';
import Header from '@/components/layout/header';

const loginSchema = z.object({
  username: z.string().min(1, 'Username is required'),
  password: z.string().min(1, 'Password is required'),
});

const registerSchema = z.object({
  username: z.string().min(3, 'Username must be at least 3 characters'),
  email: z.string().email('Invalid email address'),
  password: z.string().min(6, 'Password must be at least 6 characters'),
  confirmPassword: z.string(),
}).refine((data) => data.password === data.confirmPassword, {
  message: "Passwords don't match",
  path: ["confirmPassword"],
});

type LoginForm = z.infer<typeof loginSchema>;
type RegisterForm = z.infer<typeof registerSchema>;

export default function LoginPage() {
  const [, setLocation] = useLocation();
  const [showPassword, setShowPassword] = useState(false);
  const [isLoading, setIsLoading] = useState(false);
  const [error, setError] = useState('');
  const [activeTab, setActiveTab] = useState('login');
  const { toast } = useToast();

  const loginForm = useForm<LoginForm>({
    resolver: zodResolver(loginSchema),
    defaultValues: {
      username: '',
      password: '',
    },
  });

  const registerForm = useForm<RegisterForm>({
    resolver: zodResolver(registerSchema),
    defaultValues: {
      username: '',
      email: '',
      password: '',
      confirmPassword: '',
    },
  });

  const handleLogin = async (data: LoginForm) => {
    setIsLoading(true);
    setError('');

    try {
      // Check for admin credentials first
      if (data.username === "Admin1" && data.password === "pinpingofree...") {
        // Clear any existing session data
        localStorage.removeItem("isAdmin");
        localStorage.removeItem("adminUsername");
        localStorage.removeItem("userDisplayName");

        // Set admin role
        localStorage.setItem("role", "admin");
        localStorage.setItem("adminUsername", "Admin1");
        localStorage.setItem("userDisplayName", "Admin 1");

        toast({
          title: 'Admin Login Successful',
          description: `Welcome back, ${data.username}!`,
        });

        // Redirect to admin dashboard
        setLocation('/admin');
        return;
      }

      // For non-admin credentials, proceed with regular login
      const response = await fetch('/api/auth/login', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(data),
        credentials: 'include',
      });

      const result = await response.json();

      if (response.ok) {
        // Set user role for successful regular login
        localStorage.setItem('role', 'user');

        toast({
          title: 'Login successful',
          description: `Welcome back, ${result.user.username}!`,
        });

        // Redirect to user account dashboard
        setLocation('/account');
      } else {
        setError(result.message || 'Login failed');
      }
    } catch (error) {
      setError('Network error. Please try again.');
      console.error('Login error:', error);
    } finally {
      setIsLoading(false);
    }
  };

  const handleRegister = async (data: RegisterForm) => {
    setIsLoading(true);
    setError('');

    try {
      const response = await fetch('/api/auth/register', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          username: data.username,
          email: data.email,
          password: data.password,
        }),
        credentials: 'include',
      });

      const result = await response.json();

      if (response.ok) {
        // Set user role for successful registration
        localStorage.setItem('role', 'user');

        toast({
          title: 'Registration successful',
          description: `Welcome to Smile Pills Ltd, ${result.user.username}!`,
        });
        setLocation('/account');
      } else {
        setError(result.message || 'Registration failed');
      }
    } catch (error) {
      setError('Network error. Please try again.');
      console.error('Registration error:', error);
    } finally {
      setIsLoading(false);
    }
  };


  return (
    <div className="min-h-screen bg-gradient-to-br from-green-50 to-blue-50">
      <Header />

      <div className="container mx-auto px-4 py-16">
        <div className="max-w-md mx-auto">
          <Card className="shadow-xl border-0">
            <CardHeader className="text-center pb-6">
              <div className="mx-auto mb-4 w-16 h-16 bg-primary/10 rounded-full flex items-center justify-center">
                <Lock className="w-8 h-8 text-primary" />
              </div>
              <CardTitle className="text-2xl font-bold">Welcome to Smile Pills Ltd</CardTitle>
              <CardDescription>
                Sign in to your account or create a new one
              </CardDescription>
            </CardHeader>

            <CardContent>
              <Tabs value={activeTab} onValueChange={setActiveTab} className="space-y-6">
                <TabsList className="grid w-full grid-cols-2">
                  <TabsTrigger value="login">Sign In</TabsTrigger>
                  <TabsTrigger value="register">Register</TabsTrigger>
                </TabsList>

                {error && (
                  <Alert variant="destructive">
                    <AlertDescription>{error}</AlertDescription>
                  </Alert>
                )}

                <TabsContent value="login" className="space-y-4">
                  <Form {...loginForm}>
                    <form onSubmit={loginForm.handleSubmit(handleLogin)} className="space-y-4">
                      <FormField
                        control={loginForm.control}
                        name="username"
                        render={({ field }) => (
                          <FormItem>
                            <FormLabel>Username</FormLabel>
                            <FormControl>
                              <div className="relative">
                                <User className="absolute left-3 top-1/2 transform -translate-y-1/2 h-4 w-4 text-muted-foreground" />
                                <Input
                                  {...field}
                                  placeholder="Enter your username"
                                  className="pl-10"
                                  data-testid="input-username"
                                />
                              </div>
                            </FormControl>
                            <FormMessage />
                          </FormItem>
                        )}
                      />

                      <FormField
                        control={loginForm.control}
                        name="password"
                        render={({ field }) => (
                          <FormItem>
                            <FormLabel>Password</FormLabel>
                            <FormControl>
                              <div className="relative">
                                <Lock className="absolute left-3 top-1/2 transform -translate-y-1/2 h-4 w-4 text-muted-foreground" />
                                <Input
                                  {...field}
                                  type={showPassword ? 'text' : 'password'}
                                  placeholder="Enter your password"
                                  className="pl-10 pr-10"
                                  data-testid="input-password"
                                />
                                <button
                                  type="button"
                                  onClick={() => setShowPassword(!showPassword)}
                                  className="absolute right-3 top-1/2 transform -translate-y-1/2"
                                  data-testid="button-toggle-password"
                                >
                                  {showPassword ? (
                                    <EyeOff className="h-4 w-4 text-muted-foreground" />
                                  ) : (
                                    <Eye className="h-4 w-4 text-muted-foreground" />
                                  )}
                                </button>
                              </div>
                            </FormControl>
                            <FormMessage />
                          </FormItem>
                        )}
                      />

                      <Button 
                        type="submit" 
                        className="w-full"
                        disabled={isLoading}
                        data-testid="button-login"
                      >
                        {isLoading ? 'Signing in...' : 'Sign In'}
                      </Button>
                    </form>
                  </Form>

                </TabsContent>

                <TabsContent value="register" className="space-y-4">
                  <Form {...registerForm}>
                    <form onSubmit={registerForm.handleSubmit(handleRegister)} className="space-y-4">
                      <FormField
                        control={registerForm.control}
                        name="username"
                        render={({ field }) => (
                          <FormItem>
                            <FormLabel>Username</FormLabel>
                            <FormControl>
                              <div className="relative">
                                <User className="absolute left-3 top-1/2 transform -translate-y-1/2 h-4 w-4 text-muted-foreground" />
                                <Input
                                  {...field}
                                  placeholder="Choose a username"
                                  className="pl-10"
                                  data-testid="input-register-username"
                                />
                              </div>
                            </FormControl>
                            <FormMessage />
                          </FormItem>
                        )}
                      />

                      <FormField
                        control={registerForm.control}
                        name="email"
                        render={({ field }) => (
                          <FormItem>
                            <FormLabel>Email</FormLabel>
                            <FormControl>
                              <Input
                                {...field}
                                type="email"
                                placeholder="Enter your email"
                                data-testid="input-register-email"
                              />
                            </FormControl>
                            <FormMessage />
                          </FormItem>
                        )}
                      />

                      <FormField
                        control={registerForm.control}
                        name="password"
                        render={({ field }) => (
                          <FormItem>
                            <FormLabel>Password</FormLabel>
                            <FormControl>
                              <div className="relative">
                                <Lock className="absolute left-3 top-1/2 transform -translate-y-1/2 h-4 w-4 text-muted-foreground" />
                                <Input
                                  {...field}
                                  type={showPassword ? 'text' : 'password'}
                                  placeholder="Create a password"
                                  className="pl-10 pr-10"
                                  data-testid="input-register-password"
                                />
                                <button
                                  type="button"
                                  onClick={() => setShowPassword(!showPassword)}
                                  className="absolute right-3 top-1/2 transform -translate-y-1/2"
                                  data-testid="button-register-toggle-password"
                                >
                                  {showPassword ? (
                                    <EyeOff className="h-4 w-4 text-muted-foreground" />
                                  ) : (
                                    <Eye className="h-4 w-4 text-muted-foreground" />
                                  )}
                                </button>
                              </div>
                            </FormControl>
                            <FormMessage />
                          </FormItem>
                        )}
                      />

                      <FormField
                        control={registerForm.control}
                        name="confirmPassword"
                        render={({ field }) => (
                          <FormItem>
                            <FormLabel>Confirm Password</FormLabel>
                            <FormControl>
                              <div className="relative">
                                <Lock className="absolute left-3 top-1/2 transform -translate-y-1/2 h-4 w-4 text-muted-foreground" />
                                <Input
                                  {...field}
                                  type={showPassword ? 'text' : 'password'}
                                  placeholder="Confirm your password"
                                  className="pl-10"
                                  data-testid="input-confirm-password"
                                />
                              </div>
                            </FormControl>
                            <FormMessage />
                          </FormItem>
                        )}
                      />

                      <Button 
                        type="submit" 
                        className="w-full"
                        disabled={isLoading}
                        data-testid="button-register"
                      >
                        {isLoading ? 'Creating Account...' : 'Create Account'}
                      </Button>
                    </form>
                  </Form>
                </TabsContent>
              </Tabs>
            </CardContent>
          </Card>
        </div>
      </div>
    </div>
  );
}

client/src/pages/PaymentPage.tsx.content:
import { useState, useEffect } from "react";
import { useLocation } from "wouter";
import Header from "@/components/layout/header";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { Separator } from "@/components/ui/separator";
import { Badge } from "@/components/ui/badge";
import { useCart } from "@/hooks/useCart";
import { useAuth } from "@/hooks/useAuth";
import { useToast } from "@/hooks/use-toast";
import { 
  ShoppingCart, 
  CreditCard, 
  Lock, 
  MapPin, 
  Phone,
  Mail,
  Package,
  ArrowLeft,
  Truck
} from "lucide-react";

// Paystack types
declare global {
  interface Window {
    PaystackPop: {
      setup: (config: PaystackConfig) => {
        openIframe: () => void;
      };
    };
  }
}

interface PaystackConfig {
  key: string;
  email: string;
  amount: number;
  currency?: string;
  ref?: string;
  firstname?: string;
  lastname?: string;
  phone?: string;
  metadata?: {
    custom_fields?: Array<{
      display_name: string;
      variable_name: string;
      value: string;
    }>;
  };
  callback: (response: PaystackResponse) => void;
  onClose: () => void;
}

interface PaystackResponse {
  reference: string;
  status: string;
  trans: string;
  transaction: string;
  trxref: string;
  redirecturl: string;
}

export default function PaymentPage() {
  const [, setLocation] = useLocation();
  const { cartItems, getCartTotal, clearCart, loading } = useCart();
  const { user } = useAuth();
  const { toast } = useToast();

  const [customerInfo, setCustomerInfo] = useState({
    firstName: '',
    lastName: '',
    email: '',
    phone: '',
    address: '',
    city: 'Accra',
    region: 'Greater Accra'
  });

  const [isProcessing, setIsProcessing] = useState(false);
  const [paystackLoaded, setPaystackLoaded] = useState(false);
  const [wantsDelivery, setWantsDelivery] = useState(false);

  const totalAmount = getCartTotal();
  const deliveryFee = wantsDelivery ? 15.00 : 0.00; // GHS 15 delivery fee only if selected
  const finalAmount = totalAmount + deliveryFee;

  // Load Paystack script - optimized with preload
  useEffect(() => {
    // Check if script already exists
    if (window.PaystackPop || document.querySelector('script[src*="paystack"]')) {
      setPaystackLoaded(true);
      return;
    }

    const script = document.createElement('script');
    script.src = 'https://js.paystack.co/v1/inline.js';
    script.async = true; // Non-blocking load
    script.onload = () => setPaystackLoaded(true);
    script.onerror = () => {
      toast({
        title: "Payment System Error",
        description: "Failed to load payment system. Please try again.",
        variant: "destructive"
      });
    };
    document.head.appendChild(script); // Use head for faster loading

    return () => {
      if (script.parentNode) {
        script.parentNode.removeChild(script);
      }
    };
  }, [toast]);

  // Redirect if cart is empty - immediate check, no artificial delay
  useEffect(() => {
    if (!loading && cartItems.length === 0) {
      console.log('Cart is empty, redirecting to shop');
      toast({
        title: "Empty Cart", 
        description: "Your cart is empty. Add items before checkout.",
        variant: "destructive"
      });
      setLocation('/shop');
    }
  }, [cartItems, loading, setLocation, toast]);

  const handleInputChange = (field: string, value: string) => {
    setCustomerInfo(prev => ({
      ...prev,
      [field]: value
    }));
  };

  const generateReference = () => {
    return `SMILE_${Date.now()}_${Math.random().toString(36).substr(2, 9)}`;
  };

  // Optimized validation - cached regex and faster checks
  const emailRegex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
  const phoneRegex = /^(\+233|0)[2-9][0-9]{8}$/;

  const validateForm = () => {
    const { firstName, lastName, email, phone, address } = customerInfo;

    // Quick empty field check
    if (!firstName?.trim() || !lastName?.trim() || !email?.trim() || !phone?.trim()) {
      toast({
        title: "Missing Information",
        description: "Please fill in all required fields",
        variant: "destructive"
      });
      return false;
    }

    if (wantsDelivery && !address?.trim()) {
      toast({
        title: "Missing Address",
        description: "Please enter delivery address",
        variant: "destructive"
      });
      return false;
    }

    // Fast email validation
    if (!emailRegex.test(email)) {
      toast({
        title: "Invalid Email",
        description: "Please enter a valid email address",
        variant: "destructive"
      });
      return false;
    }

    // Fast phone validation
    if (!phoneRegex.test(phone)) {
      toast({
        title: "Invalid Phone Number",
        description: "Please enter a valid Ghana phone number",
        variant: "destructive"
      });
      return false;
    }

    return true;
  };

  const handlePayment = () => {
    // Fast validation first
    if (!validateForm()) return;

    // Immediate processing state
    setIsProcessing(true);

    if (!paystackLoaded) {
      toast({
        title: "Payment System Loading",
        description: "Please wait for the payment system to load",
        variant: "destructive"
      });
      setIsProcessing(false);
      return;
    }

    const paymentConfig: PaystackConfig = {
      key: 'pk_live_95dcd63641da880d65aba9ef1f512b48d9c58ba9',
      email: customerInfo.email,
      amount: Math.round(finalAmount * 100), // Paystack amount in pesewas (GHS * 100)
      currency: 'GHS',
      ref: generateReference(),
      firstname: customerInfo.firstName,
      lastname: customerInfo.lastName,
      phone: customerInfo.phone,
      metadata: {
        custom_fields: [
          {
            display_name: "Order Summary",
            variable_name: "order_summary",
            value: `${cartItems.length} items - ${wantsDelivery ? 'Delivery' : 'Pickup'}`
          },
          {
            display_name: "Customer",
            variable_name: "customer",
            value: `${customerInfo.firstName} ${customerInfo.lastName}`
          }
        ]
      },
      callback: (response: PaystackResponse) => {
        setIsProcessing(false);

        if (response.status === 'success') {
          // Optimistic UI update - clear cart immediately
          clearCart();

          // Create order record asynchronously
          setTimeout(() => {
            const orderData = {
              reference: response.reference,
              customerInfo,
              items: cartItems,
              totalAmount: finalAmount,
              paymentStatus: 'completed',
              deliveryStatus: 'pending'
            };

            try {
              const orders = JSON.parse(localStorage.getItem('orders') || '[]');
              orders.push({
                ...orderData,
                id: response.reference,
                createdAt: new Date().toISOString()
              });
              localStorage.setItem('orders', JSON.stringify(orders));
            } catch (error) {
              console.error('Error saving order:', error);
            }
          }, 0); // Non-blocking operation

          // Success alert and redirect
          alert(`Payment successful! 
Reference: ${response.reference}
Amount: GHS ${finalAmount.toFixed(2)}

Your order has been placed and you will receive a confirmation shortly.
Delivery within 24-48 hours in Accra.`);

          // Redirect to success page or home
          setLocation('/');

          toast({
            title: "Payment Successful!",
            description: `Order ${response.reference} placed successfully`,
          });
        } else {
          toast({
            title: "Payment Failed",
            description: "Your payment was not completed. Please try again.",
            variant: "destructive"
          });
        }
      },
      onClose: () => {
        setIsProcessing(false);
        alert('Payment cancelled. Your order has not been placed.');
      }
    };

    try {
      const paystack = window.PaystackPop.setup(paymentConfig);
      paystack.openIframe();
    } catch (error) {
      setIsProcessing(false);
      console.error('Paystack error:', error);
      toast({
        title: "Payment Error",
        description: "Failed to initialize payment. Please try again.",
        variant: "destructive"
      });
    }
  };

  const handleWhatsAppOrder = () => {
    const orderSummary = cartItems.map(item => 
      `${item.quantity}x ${item.name} - GHS ${(item.price * item.quantity).toFixed(2)}`
    ).join('\n');

    const message = `🛒 *New Order from Smile Pills Ltd*

*Customer Details:*
Name: ${customerInfo.firstName} ${customerInfo.lastName}
Email: ${customerInfo.email}
Phone: ${customerInfo.phone}
${wantsDelivery ? `Address: ${customerInfo.address}, ${customerInfo.city}` : 'Pickup: Customer will collect from store'}

*Order Summary:*
${orderSummary}

*Total:* GHS ${totalAmount.toFixed(2)}
${wantsDelivery ? `*Delivery:* GHS ${deliveryFee.toFixed(2)}\n` : '*Pickup:* Free (Customer pickup)\n'}*Final Total:* GHS ${finalAmount.toFixed(2)}

*Payment Method:* Cash on ${wantsDelivery ? 'Delivery' : 'Pickup'}

Please confirm this order and provide delivery time.`;

    const whatsappUrl = `https://wa.me/233209339912?text=${encodeURIComponent(message)}`;
    window.open(whatsappUrl, '_blank');

    toast({
      title: "Order Sent via WhatsApp",
      description: "Your order has been sent. We'll confirm delivery details shortly."
    });
  };

  // Debug logging
  console.log('PaymentPage cartItems:', cartItems);
  console.log('PaymentPage totalAmount:', totalAmount);

  if (loading) {
    return (
      <div className="min-h-screen bg-gray-50">
        <Header />
        <div className="flex items-center justify-center py-20">
          <div className="text-center">
            <h2 className="text-2xl font-bold mb-4">Loading Cart...</h2>
            <p className="text-gray-600">Please wait while we load your cart items.</p>
          </div>
        </div>
      </div>
    );
  }

  if (cartItems.length === 0) {
    return (
      <div className="min-h-screen bg-gray-50">
        <Header />
        <div className="flex items-center justify-center py-20">
          <div className="text-center">
            <h2 className="text-2xl font-bold mb-4">Cart is Empty</h2>
            <p className="text-gray-600 mb-4">Your cart is empty. Add items before checkout.</p>
            <Button onClick={() => setLocation('/shop')}>Browse Products</Button>
          </div>
        </div>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-gray-50">
      <Header />

      <div className="py-8">
        <div className="max-w-6xl mx-auto px-4">
          {/* Header */}
          <div className="mb-8">
            <Button 
              variant="ghost" 
              onClick={() => setLocation('/cart')}
              className="mb-4 flex items-center gap-2"
            >
              <ArrowLeft className="h-4 w-4" />
              Back to Cart
            </Button>
            <h1 className="text-3xl font-bold text-neutral mb-2">Checkout</h1>
            <p className="text-gray-600">Complete your order with secure payment</p>
          </div>

          <div className="grid lg:grid-cols-2 gap-8">
            {/* Customer Information */}
            <div className="space-y-6">
              <Card>
                <CardHeader>
                  <CardTitle className="flex items-center gap-2">
                    <Mail className="h-5 w-5" />
                    Customer Information
                  </CardTitle>
                </CardHeader>
                <CardContent className="space-y-4">
                  <div className="grid grid-cols-2 gap-4">
                    <div>
                      <Label htmlFor="firstName">First Name *</Label>
                      <Input
                        id="firstName"
                        value={customerInfo.firstName}
                        onChange={(e) => handleInputChange('firstName', e.target.value)}
                        placeholder="Enter first name"
                      />
                    </div>
                    <div>
                      <Label htmlFor="lastName">Last Name *</Label>
                      <Input
                        id="lastName"
                        value={customerInfo.lastName}
                        onChange={(e) => handleInputChange('lastName', e.target.value)}
                        placeholder="Enter last name"
                      />
                    </div>
                  </div>

                  <div>
                    <Label htmlFor="email">Email Address *</Label>
                    <Input
                      id="email"
                      type="email"
                      value={customerInfo.email}
                      onChange={(e) => handleInputChange('email', e.target.value)}
                      placeholder="your@email.com"
                    />
                  </div>

                  <div>
                    <Label htmlFor="phone">Phone Number *</Label>
                    <Input
                      id="phone"
                      value={customerInfo.phone}
                      onChange={(e) => handleInputChange('phone', e.target.value)}
                      placeholder="+233 XX XXX XXXX"
                    />
                  </div>
                </CardContent>
              </Card>

              <Card>
                <CardHeader>
                  <CardTitle className="flex items-center gap-2">
                    <Truck className="h-5 w-5" />
                    Delivery Option
                  </CardTitle>
                </CardHeader>
                <CardContent>
                  <div className="flex items-center space-x-2">
                    <input
                      type="checkbox"
                      id="wantsDelivery"
                      checked={wantsDelivery}
                      onChange={(e) => setWantsDelivery(e.target.checked)}
                      className="h-4 w-4"
                    />
                    <Label htmlFor="wantsDelivery" className="text-sm font-normal">
                      I want delivery (+GHS 15.00)
                    </Label>
                  </div>
                  <p className="text-xs text-gray-600 mt-2">
                    {wantsDelivery 
                      ? "📦 We'll deliver to your address within 24-48 hours" 
                      : "🏪 You can collect your order from our store for free"
                    }
                  </p>
                </CardContent>
              </Card>

              {wantsDelivery && (
                <Card>
                  <CardHeader>
                    <CardTitle className="flex items-center gap-2">
                      <MapPin className="h-5 w-5" />
                      Delivery Address
                    </CardTitle>
                  </CardHeader>
                  <CardContent className="space-y-4">
                    <div>
                      <Label htmlFor="address">Street Address *</Label>
                      <Input
                        id="address"
                        value={customerInfo.address}
                        onChange={(e) => handleInputChange('address', e.target.value)}
                        placeholder="House number, street name"
                      />
                    </div>

                    <div className="grid grid-cols-2 gap-4">
                      <div>
                        <Label htmlFor="city">City</Label>
                        <Input
                          id="city"
                          value={customerInfo.city}
                          onChange={(e) => handleInputChange('city', e.target.value)}
                        />
                      </div>
                      <div>
                        <Label htmlFor="region">Region</Label>
                        <Input
                          id="region"
                          value={customerInfo.region}
                          onChange={(e) => handleInputChange('region', e.target.value)}
                        />
                      </div>
                    </div>
                  </CardContent>
                </Card>
              )}
            </div>

            {/* Order Summary */}
            <div className="space-y-6">
              <Card>
                <CardHeader>
                  <CardTitle className="flex items-center gap-2">
                    <ShoppingCart className="h-5 w-5" />
                    Order Summary
                  </CardTitle>
                </CardHeader>
                <CardContent>
                  <div className="space-y-4">
                    {cartItems.map((item) => (
                      <div key={item.id} className="flex justify-between items-center">
                        <div className="flex-1">
                          <p className="font-medium">{item.name}</p>
                          <p className="text-sm text-gray-600">
                            Qty: {item.quantity} × GHS {item.price.toFixed(2)}
                          </p>
                        </div>
                        <p className="font-semibold">
                          GHS {(item.price * item.quantity).toFixed(2)}
                        </p>
                      </div>
                    ))}

                    <Separator />

                    <div className="space-y-2">
                      <div className="flex justify-between">
                        <span>Subtotal</span>
                        <span>GHS {totalAmount.toFixed(2)}</span>
                      </div>
                      <div className="flex justify-between">
                        <span>{wantsDelivery ? 'Delivery Fee' : 'Pickup (Free)'}</span>
                        <span>{wantsDelivery ? `GHS ${deliveryFee.toFixed(2)}` : 'FREE'}</span>
                      </div>
                      <Separator />
                      <div className="flex justify-between text-lg font-bold">
                        <span>Total</span>
                        <span className="text-primary">GHS {finalAmount.toFixed(2)}</span>
                      </div>
                    </div>
                  </div>
                </CardContent>
              </Card>

              {/* Payment Methods */}
              <Card>
                <CardHeader>
                  <CardTitle className="flex items-center gap-2">
                    <CreditCard className="h-5 w-5" />
                    Payment Method
                  </CardTitle>
                </CardHeader>
                <CardContent className="space-y-4">
                  <div className="p-4 border rounded-lg bg-blue-50">
                    <div className="flex items-center gap-2 mb-2">
                      <Lock className="h-4 w-4 text-blue-600" />
                      <span className="font-medium">Secure Online Payment</span>
                      <Badge variant="secondary">Recommended</Badge>
                    </div>
                    <p className="text-sm text-gray-600 mb-4">
                      Pay securely with Mobile Money, Visa, Mastercard via Paystack
                    </p>
                    <Button 
                      onClick={handlePayment}
                      disabled={isProcessing || !paystackLoaded}
                      className="w-full flex items-center gap-2"
                      size="lg"
                    >
                      <CreditCard className="h-4 w-4" />
                      {isProcessing ? 'Processing...' : `Pay GHS ${finalAmount.toFixed(2)}`}
                    </Button>
                  </div>

                  <div className="text-center">
                    <span className="text-gray-500">or</span>
                  </div>

                  <div className="p-4 border rounded-lg">
                    <div className="flex items-center gap-2 mb-2">
                      <Phone className="h-4 w-4 text-green-600" />
                      <span className="font-medium">Cash on Delivery</span>
                    </div>
                    <p className="text-sm text-gray-600 mb-4">
                      Order via WhatsApp and pay when you receive your items
                    </p>
                    <Button 
                      onClick={handleWhatsAppOrder}
                      variant="outline"
                      className="w-full flex items-center gap-2"
                      size="lg"
                    >
                      <Package className="h-4 w-4" />
                      Order via WhatsApp
                    </Button>
                  </div>

                  <div className="text-xs text-gray-500 text-center mt-4">
                    <p>🔒 Your payment information is secure and encrypted</p>
                    <p>📦 Free delivery within Accra • 24-48 hour delivery</p>
                  </div>
                </CardContent>
              </Card>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}

