import { useState, useEffect } from "react";
import { Search, Grid, List, Filter, ChevronDown, ShoppingCart } from "lucide-react";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { Badge } from "@/components/ui/badge";
import { Card, CardContent } from "@/components/ui/card";
import * as XLSX from 'xlsx';
import { useCart } from '@/hooks/useCart';
import { useToast } from '@/hooks/use-toast';
import Header from '@/components/layout/header';
import { useProducts, type Product } from '@/contexts/ProductContext';

interface ProductCardProps {
  product: any;
  viewMode: 'grid' | 'list';
}

function ProductCard({ product, viewMode }: ProductCardProps) {
  const [imageError, setImageError] = useState(false);
  const { addToCart, isInCart } = useCart();
  const { toast } = useToast();

  // Safely get product details with fallbacks for both formats
  const productName = product['Product Name'] || '';
  const productBrand = product.Brand || '';
  const productCategory = product.Category || '';
  const productPrice = parseFloat(product.Price?.toString() || '0') || 0;
  const productImageUrl = product.ImageURL || '';

  const handleAddToCart = () => {
    addToCart({
      id: String(product.id),
      'Product Name': product['Product Name'],
      Category: product.Category,
      Brand: product.Brand,
      Price: product.Price,
      ImageURL: product.ImageURL,
    });
    toast({
      title: "Added to cart",
      description: `${productName} has been added to your cart.`,
    });
  };

  if (viewMode === 'list') {
    return (
      <Card className="hover:shadow-lg transition-shadow duration-200">
        <CardContent className="p-2">
          <div className="flex gap-4">
            <div className="w-12 h-12 flex-shrink-0">
              {!imageError && productImageUrl ? (
                <img
                  src={productImageUrl}
                  alt={productName}
                  className="w-full h-full object-contain rounded-md p-1"
                  onError={(e) => {
                    console.log('Image load error for:', productName, 'URL:', productImageUrl);
                    setImageError(true);
                  }}
                  onLoad={() => console.log('Image loaded successfully:', productName)}
                />
              ) : (
                <div className="w-full h-full bg-gradient-to-br from-blue-50 to-green-50 rounded-md flex flex-col items-center justify-center p-2">
                  <div className="w-8 h-8 bg-secondary/20 rounded-full flex items-center justify-center mb-1">
                    <ShoppingCart className="h-4 w-4 text-secondary" />
                  </div>
                  <span className="text-xs text-gray-600 text-center leading-tight">
                    {productBrand}
                  </span>
                </div>
              )}
            </div>
            <div className="flex-1">
              <h3 className="font-semibold text-gray-900 dark:text-white line-clamp-2 mb-1">
                {productName}
              </h3>
              <p className="text-sm text-gray-600 dark:text-gray-400 mb-2">
                {productBrand}
              </p>
              <div className="flex items-center justify-between">
                <span className="text-lg font-bold text-secondary">
                  ₵{productPrice.toFixed(2)}
                </span>
                <Button
                  size="sm"
                  onClick={handleAddToCart}
                  className="bg-secondary hover:bg-secondary/90"
                  data-testid={`button-add-cart-list-${productName.toLowerCase().replace(/[^a-z0-9]/g, '-')}`}
                >
                  <ShoppingCart className="h-4 w-4 mr-1" />
                  {isInCart(product) ? 'Added' : 'Add to Cart'}
                </Button>
              </div>
            </div>
          </div>
        </CardContent>
      </Card>
    );
  }

  return (
    <Card className="group hover:shadow-lg transition-all duration-200 overflow-hidden">
      <CardContent className="p-0">
        <div className="aspect-square w-full">
          {!imageError && productImageUrl ? (
            <img
              src={productImageUrl}
              alt={productName}
              className="w-full h-full object-contain group-hover:scale-105 transition-transform duration-200 p-8"
              onError={(e) => {
                console.log('Image load error for:', productName, 'URL:', productImageUrl);
                setImageError(true);
              }}
              onLoad={() => console.log('Image loaded successfully:', productName)}
            />
          ) : (
            <div className="w-full h-full bg-gradient-to-br from-blue-50 to-green-50 flex flex-col items-center justify-center p-5">
              <div className="w-20 h-20 bg-secondary/20 rounded-full flex items-center justify-center mb-4">
                <ShoppingCart className="h-10 w-10 text-secondary" />
              </div>
              <span className="text-sm text-gray-600 text-center font-medium">
                {productBrand}
              </span>
              <span className="text-xs text-gray-500 text-center mt-1">
                Product Image
              </span>
            </div>
          )}
        </div>
        <div className="p-3">
          <h3 className="font-medium text-gray-900 dark:text-white line-clamp-2 mb-2 min-h-[2rem] text-sm">
            {productName}
          </h3>
          <p className="text-xs text-gray-500 dark:text-gray-500 mb-2 truncate">
            {productBrand}
          </p>
          <div className="flex items-center justify-between">
            <span className="text-sm font-bold text-secondary">
              ₵{productPrice.toFixed(2)}
            </span>
            <Button
              size="sm"
              onClick={handleAddToCart}
              className={`h-7 px-3 text-xs ${
                isInCart(product) 
                  ? 'bg-green-600 hover:bg-green-700 text-white' 
                  : 'bg-secondary hover:bg-secondary/90'
              }`}
              data-testid={`button-add-cart-grid-${productName.toLowerCase().replace(/[^a-z0-9]/g, '-')}`}
            >
              <ShoppingCart className="h-3 w-3 mr-1" />
              {isInCart(product) ? 'Added' : 'Add'}
            </Button>
          </div>
        </div>
      </CardContent>
    </Card>
  );
}

export function ShopPage() {
  const [viewMode, setViewMode] = useState<'grid' | 'list'>('grid');
  const [sortBy, setSortBy] = useState<'name' | 'price' | 'brand'>('name');

  // Use ProductContext for shared state management
  const { products, categories: allCategories, customCategories } = useProducts();

  const [searchQuery, setSearchQuery] = useState('');
  const [selectedCategory, setSelectedCategory] = useState('All');

  // Filter products based on search and category
  const filteredProducts = products.filter((product: any) => {
    const name = product['Product Name'] || '';
    const category = product.Category || '';
    const brand = product.Brand || '';

    // Search filter
    const matchesSearch = !searchQuery || 
      name.toLowerCase().includes(searchQuery.toLowerCase()) ||
      category.toLowerCase().includes(searchQuery.toLowerCase()) ||
      brand.toLowerCase().includes(searchQuery.toLowerCase());

    // Category filter  
    const matchesCategory = selectedCategory === 'All' || category === selectedCategory;

    return matchesSearch && matchesCategory;
  });

  // Use categories from context to ensure consistency - force update when context changes
  const categories: string[] = allCategories || [];

  // Force re-render when categories change
  useEffect(() => {
    console.log('ShopPage: Categories updated:', categories);
  }, [categories, customCategories]);

  // Apply sorting only when specifically requested, otherwise maintain context order (newest first)
  const displayProducts = sortBy === 'name' && searchQuery === '' && selectedCategory === 'All' 
    ? filteredProducts // Maintain context order for default state
    : [...filteredProducts].sort((a, b) => {
        const priceA = parseFloat(a.Price?.toString() || '0');
        const priceB = parseFloat(b.Price?.toString() || '0');
        const nameA = a['Product Name'] || '';
        const nameB = b['Product Name'] || '';
        const brandA = a.Brand || '';
        const brandB = b.Brand || '';

        switch (sortBy) {
          case 'price':
            return priceA - priceB;
          case 'brand':
            return brandA.localeCompare(brandB);
          case 'name':
          default:
            return nameA.localeCompare(nameB);
        }
      });


  // Group products by category for "All" view
  const groupedProducts: Record<string, any[]> = categories.reduce((acc: Record<string, any[]>, category: string) => {
    acc[category] = displayProducts.filter((p: any) => p.Category === category);
    return acc;
  }, {} as Record<string, any[]>);

  if (!products || products.length === 0) {
    return (
      <div className="min-h-screen bg-gray-50 dark:bg-gray-900">
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-8">
          <div className="flex items-center justify-center min-h-96">
            <div className="text-center">
              <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-secondary mx-auto mb-4"></div>
              <p className="text-gray-600 dark:text-gray-400">Loading products...</p>
            </div>
          </div>
        </div>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-gray-50 dark:bg-gray-900">
      {/* Header */}
      <Header />

      {/* Hero Section */}
      <div className="bg-white dark:bg-gray-800 shadow-sm">
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-12">
          <div className="text-center">
            <h1 className="text-3xl md:text-4xl font-bold text-gray-900 dark:text-white mb-4">
              Shop Our Products
            </h1>
            <p className="text-lg text-gray-600 dark:text-gray-400 max-w-2xl mx-auto">
              Browse our comprehensive collection of pharmaceutical products and health supplements
            </p>
          </div>
        </div>
      </div>

      {/* Filters Bar */}
      <div className="bg-white dark:bg-gray-800 border-b border-gray-200 dark:border-gray-700 sticky top-16 z-30">
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-4">
          <div className="flex flex-col md:flex-row gap-4 items-center justify-between">
            {/* Search */}
            <div className="relative flex-1 max-w-md">
              <Search className="absolute left-3 top-1/2 transform -translate-y-1/2 text-gray-400 h-4 w-4" />
              <Input
                type="text"
                placeholder="Search products..."
                value={searchQuery}
                onChange={(e) => setSearchQuery(e.target.value)}
                className="pl-10"
                data-testid="input-search"
              />
            </div>

            <div className="flex items-center gap-4">
              {/* Category Filter */}
              <div className="min-w-48">
                <Select value={selectedCategory} onValueChange={setSelectedCategory}>
                  <SelectTrigger data-testid="select-category">
                    <SelectValue />
                  </SelectTrigger>
                  <SelectContent>
                    <SelectItem value="All">All Categories</SelectItem>
                    {categories.map((category: string) => (
                      <SelectItem key={category} value={category}>
                        {category}
                      </SelectItem>
                    ))}
                  </SelectContent>
                </Select>
              </div>

              {/* Sort By */}
              <div className="min-w-40">
                <Select value={sortBy} onValueChange={(value: 'name' | 'price' | 'brand') => setSortBy(value)}>
                  <SelectTrigger data-testid="select-sort">
                    <SelectValue />
                  </SelectTrigger>
                  <SelectContent>
                    <SelectItem value="name">Product Name</SelectItem>
                    <SelectItem value="price">Price</SelectItem>
                    <SelectItem value="brand">Brand</SelectItem>
                  </SelectContent>
                </Select>
              </div>

              {/* View Mode Toggle */}
              <div className="flex border border-gray-200 dark:border-gray-700 rounded-md">
                <Button
                  variant={viewMode === 'grid' ? 'default' : 'ghost'}
                  size="sm"
                  onClick={() => setViewMode('grid')}
                  className="rounded-r-none"
                  data-testid="button-view-grid"
                >
                  <Grid className="h-4 w-4" />
                </Button>
                <Button
                  variant={viewMode === 'list' ? 'default' : 'ghost'}
                  size="sm"
                  onClick={() => setViewMode('list')}
                  className="rounded-l-none"
                  data-testid="button-view-list"
                >
                  <List className="h-4 w-4" />
                </Button>
              </div>
            </div>
          </div>
        </div>
      </div>

      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-8">
        {/* Category Summary Chips */}
        <div className="mb-6">
          <div className="flex flex-wrap gap-2">
            <Button
              variant={selectedCategory === 'All' ? 'default' : 'outline'}
              size="sm"
              onClick={() => setSelectedCategory('All')}
              className="text-xs"
            >
              All ({products.length})
            </Button>
            {categories.slice(0, 8).map(category => {
              const count = products.filter((p: any) => p.Category === category).length;
              return (
                <Button
                  key={category}
                  variant={selectedCategory === category ? 'default' : 'outline'}
                  size="sm"
                  onClick={() => setSelectedCategory(category)}
                  className="text-xs"
                >
                  {category} ({count})
                </Button>
              );
            })}
            {categories.length > 8 && (
              <Button
                variant="outline"
                size="sm"
                className="text-xs opacity-50"
                disabled
              >
                +{categories.length - 8} more
              </Button>
            )}
          </div>
        </div>



        {/* Products Grid */}
        <div>
          {displayProducts.length === 0 ? (
            <div className="text-center py-12">
              <div className="text-gray-400 mb-4">
                <Search className="h-12 w-12 mx-auto mb-4" />
              </div>
              <h3 className="text-lg font-semibold text-gray-900 dark:text-white mb-2">
                No products found
              </h3>
              <p className="text-gray-600 dark:text-gray-400 mb-4">
                Try adjusting your search or filter criteria
              </p>
              <Button
                onClick={() => {
                  setSearchQuery('');
                  setSelectedCategory('All');
                }}
                variant="outline"
              >
                Clear Filters
              </Button>
            </div>
          ) : selectedCategory !== 'All' ? (
            /* Single Category View */
            <div>
              <div className="flex items-center justify-between mb-6">
                <h2 className="text-2xl font-bold text-gray-900 dark:text-white">
                  {selectedCategory}
                </h2>
                <Badge variant="outline" data-testid="text-product-count">
                  {displayProducts.length} products
                </Badge>
              </div>

              <div className={`grid gap-6 mt-6 mb-8 ${viewMode === 'grid' ? 'grid-cols-2 sm:grid-cols-3 md:grid-cols-4' : 'grid-cols-1'}`}>
                {displayProducts.map((product: any, index: number) => (
                  <ProductCard key={`${product.id || product.name || product['Product Name']}-${index}`} product={product} viewMode={viewMode} />
                ))}
              </div>
            </div>
          ) : (
            /* All Categories View - Grouped */
            <div className="space-y-12">
              {Object.entries(groupedProducts).map(([category, categoryProducts]: [string, any[]]) => (
                categoryProducts.length > 0 && (
                  <div key={category}>
                    <div className="flex items-center justify-between mb-6">
                      <h2 className="text-2xl font-bold text-gray-900 dark:text-white">
                        {category}
                      </h2>
                      <Badge variant="outline" data-testid={`text-category-${category.toLowerCase().replace(/[^a-z0-9]/g, '-')}-count`}>
                        {categoryProducts.length} products
                      </Badge>
                    </div>

                    <div className={`grid gap-6 mt-6 mb-8 ${viewMode === 'grid' ? 'grid-cols-2 sm:grid-cols-3 md:grid-cols-4' : 'grid-cols-1'}`}>
                      {categoryProducts.map((product: any, index: number) => (
                        <ProductCard key={`${product.id || product.name || product['Product Name']}-${index}`} product={product} viewMode={viewMode} />
                      ))}
                    </div>
                  </div>
                )
              ))}
            </div>
          )}
        </div>
      </div>
    </div>
  );
}

client/src/pages/UserDashboard.tsx.content:
import { useEffect, useState } from "react";
import { useAuth } from "@/hooks/useAuth";
import { useLocation } from "wouter";
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Badge } from "@/components/ui/badge";
import Header from "@/components/layout/header";
import { 
  User, 
  ShoppingCart, 
  FileText, 
  Clock, 
  Package,
  Heart,
  Settings,
  Bell
} from "lucide-react";

export default function UserDashboard() {
  const { user, isAuthenticated } = useAuth();
  const [, setLocation] = useLocation();
  const [recentOrders, setRecentOrders] = useState([]);
  const [recentPrescriptions, setRecentPrescriptions] = useState([]);

  useEffect(() => {
    const userRole = localStorage.getItem('role');

    if (!isAuthenticated && userRole !== 'user') {
      setLocation('/login');
      return;
    }

    // Load user data
    loadUserData();
  }, [isAuthenticated, setLocation]);

  const loadUserData = async () => {
    try {
      // Load recent orders
      const ordersResponse = await fetch('/api/orders', {
        credentials: 'include'
      });
      if (ordersResponse.ok) {
        const ordersData = await ordersResponse.json();
        setRecentOrders(ordersData.slice(0, 3)); // Show last 3 orders
      }

      // Load recent prescriptions
      const prescriptionsResponse = await fetch('/api/prescriptions/history', {
        credentials: 'include'
      });
      if (prescriptionsResponse.ok) {
        const prescriptionsData = await prescriptionsResponse.json();
        setRecentPrescriptions(prescriptionsData.slice(0, 3)); // Show last 3 prescriptions
      }
    } catch (error) {
      console.error('Error loading user data:', error);
    }
  };

  if (!isAuthenticated) {
    return null;
  }

  const getUserDisplayName = () => {
    if (user?.firstName && user?.lastName) {
      return `${user.firstName} ${user.lastName}`;
    }
    return user?.username || user?.email || 'User';
  };

  return (
    <div className="min-h-screen bg-gray-50">
      <Header />

      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-8">
        {/* Welcome Section */}
        <div className="mb-8">
          <h1 className="text-3xl font-bold text-gray-900 mb-2">
            Welcome back, {getUserDisplayName()}!
          </h1>
          <p className="text-gray-600">
            Manage your health journey with Smile Pills Ltd
          </p>
        </div>

        {/* Quick Stats */}
        <div className="grid grid-cols-1 md:grid-cols-4 gap-6 mb-8">
          <Card>
            <CardContent className="p-6">
              <div className="flex items-center">
                <ShoppingCart className="h-8 w-8 text-primary" />
                <div className="ml-4">
                  <p className="text-sm font-medium text-gray-600">Total Orders</p>
                  <p className="text-2xl font-bold text-gray-900">{recentOrders.length}</p>
                </div>
              </div>
            </CardContent>
          </Card>

          <Card>
            <CardContent className="p-6">
              <div className="flex items-center">
                <FileText className="h-8 w-8 text-green-600" />
                <div className="ml-4">
                  <p className="text-sm font-medium text-gray-600">Prescriptions</p>
                  <p className="text-2xl font-bold text-gray-900">{recentPrescriptions.length}</p>
                </div>
              </div>
            </CardContent>
          </Card>

          <Card>
            <CardContent className="p-6">
              <div className="flex items-center">
                <Heart className="h-8 w-8 text-red-600" />
                <div className="ml-4">
                  <p className="text-sm font-medium text-gray-600">Saved Items</p>
                  <p className="text-2xl font-bold text-gray-900">0</p>
                </div>
              </div>
            </CardContent>
          </Card>

          <Card>
            <CardContent className="p-6">
              <div className="flex items-center">
                <Package className="h-8 w-8 text-blue-600" />
                <div className="ml-4">
                  <p className="text-sm font-medium text-gray-600">Active Orders</p>
                  <p className="text-2xl font-bold text-gray-900">0</p>
                </div>
              </div>
            </CardContent>
          </Card>
        </div>

        <div className="grid grid-cols-1 lg:grid-cols-2 gap-8">
          {/* Recent Orders */}
          <Card>
            <CardHeader>
              <CardTitle className="flex items-center gap-2">
                <ShoppingCart className="h-5 w-5" />
                Recent Orders
              </CardTitle>
              <CardDescription>
                Your latest medication orders
              </CardDescription>
            </CardHeader>
            <CardContent>
              {recentOrders.length > 0 ? (
                <div className="space-y-4">
                  {recentOrders.map((order: any, index) => (
                    <div key={index} className="flex items-center justify-between p-4 bg-gray-50 rounded-lg">
                      <div>
                        <p className="font-medium">Order #{order.id || index + 1}</p>
                        <p className="text-sm text-gray-600">
                          {new Date(order.createdAt || Date.now()).toLocaleDateString()}
                        </p>
                      </div>
                      <Badge variant="secondary">
                        {order.status || 'Processing'}
                      </Badge>
                    </div>
                  ))}
                </div>
              ) : (
                <div className="text-center py-8">
                  <ShoppingCart className="h-12 w-12 text-gray-400 mx-auto mb-4" />
                  <p className="text-gray-600">No orders yet</p>
                  <Button 
                    className="mt-4" 
                    onClick={() => setLocation('/shop')}
                  >
                    Start Shopping
                  </Button>
                </div>
              )}
            </CardContent>
          </Card>

          {/* Recent Prescriptions */}
          <Card>
            <CardHeader>
              <CardTitle className="flex items-center gap-2">
                <FileText className="h-5 w-5" />
                Recent Prescriptions
              </CardTitle>
              <CardDescription>
                Your uploaded prescription history
              </CardDescription>
            </CardHeader>
            <CardContent>
              {recentPrescriptions.length > 0 ? (
                <div className="space-y-4">
                  {recentPrescriptions.map((prescription: any, index) => (
                    <div key={index} className="flex items-center justify-between p-4 bg-gray-50 rounded-lg">
                      <div>
                        <p className="font-medium">Prescription #{prescription.id || index + 1}</p>
                        <p className="text-sm text-gray-600">
                          {new Date(prescription.createdAt || Date.now()).toLocaleDateString()}
                        </p>
                      </div>
                      <Badge variant="outline">
                        {prescription.status || 'Pending'}
                      </Badge>
                    </div>
                  ))}
                </div>
              ) : (
                <div className="text-center py-8">
                  <FileText className="h-12 w-12 text-gray-400 mx-auto mb-4" />
                  <p className="text-gray-600">No prescriptions uploaded</p>
                  <Button 
                    className="mt-4" 
                    onClick={() => setLocation('/prescription')}
                  >
                    Upload Prescription
                  </Button>
                </div>
              )}
            </CardContent>
          </Card>
        </div>

        {/* Quick Actions */}
        <Card className="mt-8">
          <CardHeader>
            <CardTitle>Quick Actions</CardTitle>
            <CardDescription>
              Common tasks to help manage your health
            </CardDescription>
          </CardHeader>
          <CardContent>
            <div className="grid grid-cols-2 md:grid-cols-4 gap-4">
              <Button
                variant="outline"
                className="h-20 flex-col"
                onClick={() => setLocation('/shop')}
              >
                <ShoppingCart className="h-6 w-6 mb-2" />
                Shop Medications
              </Button>

              <Button
                variant="outline"
                className="h-20 flex-col"
                onClick={() => setLocation('/prescription')}
              >
                <FileText className="h-6 w-6 mb-2" />
                Upload Prescription
              </Button>

              <Button
                variant="outline"
                className="h-20 flex-col"
                onClick={() => setLocation('/cart')}
              >
                <Package className="h-6 w-6 mb-2" />
                View Cart
              </Button>

              <Button
                variant="outline"
                className="h-20 flex-col"
                onClick={() => setLocation('/contact')}
              >
                <Bell className="h-6 w-6 mb-2" />
                Contact Support
              </Button>
            </div>
          </CardContent>
        </Card>
      </div>
    </div>
  );
}

client/src/pages/browse.tsx.content:
import { useState, useEffect, useMemo } from "react";
import { Link } from "wouter";
import { Search, Filter, ChevronUp, Grid, List } from "lucide-react";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Card, CardContent } from "@/components/ui/card";
import { Badge } from "@/components/ui/badge";
import Header from "@/components/layout/header";
import {
  Select,
  SelectContent,
  SelectItem,
  SelectTrigger,
  SelectValue,
} from "@/components/ui/select";
import { useQuery } from "@tanstack/react-query";

interface Product {
  id: string;
  name: string;
  slug: string;
  brand?: { name: string };
  category?: { name: string; slug: string };
  price: string;
  originalPrice?: string;
  imageUrl: string;
  stockQuantity: number;
  requiresPrescription: boolean;
  rating: string;
  reviewCount: number;
  shortDescription?: string;
}

interface Category {
  id: string;
  name: string;
  slug: string;
  description: string;
}

interface Brand {
  id: string;
  name: string;
}

type SortOption = 'relevant' | 'name' | 'price-low' | 'price-high' | 'rating';
type ViewMode = 'grid' | 'list';

export default function BrowsePage() {
  const [searchQuery, setSearchQuery] = useState("");
  const [selectedCategory, setSelectedCategory] = useState<string>("all");
  const [selectedBrand, setSelectedBrand] = useState<string>("all");
  const [sortBy, setSortBy] = useState<SortOption>('relevant');
  const [viewMode, setViewMode] = useState<ViewMode>('grid');
  const [showInStockOnly, setShowInStockOnly] = useState(false);
  const [showBackToTop, setShowBackToTop] = useState(false);

  // Fetch products
  const { data: products = [], isLoading: productsLoading } = useQuery<Product[]>({
    queryKey: ['/api/products'],
  });

  // Fetch categories
  const { data: categories = [] } = useQuery<Category[]>({
    queryKey: ['/api/categories'],
  });

  // Fetch brands
  const { data: brands = [] } = useQuery<Brand[]>({
    queryKey: ['/api/brands'],
  });

  // Handle scroll for back to top button
  useEffect(() => {
    const handleScroll = () => {
      setShowBackToTop(window.scrollY > 400);
    };
    window.addEventListener('scroll', handleScroll);
    return () => window.removeEventListener('scroll', handleScroll);
  }, []);

  // Filter and sort products
  const filteredProducts = useMemo(() => {
    let filtered = products.filter(product => {
      const matchesSearch = !searchQuery || 
        product.name.toLowerCase().includes(searchQuery.toLowerCase()) ||
        product.brand?.name.toLowerCase().includes(searchQuery.toLowerCase());

      const matchesCategory = selectedCategory === 'all' || 
        product.category?.slug === selectedCategory;

      const matchesBrand = selectedBrand === 'all' || 
        product.brand?.name === selectedBrand;

      const matchesStock = !showInStockOnly || product.stockQuantity > 0;

      return matchesSearch && matchesCategory && matchesBrand && matchesStock;
    });

    // Sort products
    switch (sortBy) {
      case 'name':
        filtered.sort((a, b) => a.name.localeCompare(b.name));
        break;
      case 'price-low':
        filtered.sort((a, b) => parseFloat(a.price) - parseFloat(b.price));
        break;
      case 'price-high':
        filtered.sort((a, b) => parseFloat(b.price) - parseFloat(a.price));
        break;
      case 'rating':
        filtered.sort((a, b) => parseFloat(b.rating) - parseFloat(a.rating));
        break;
    }

    return filtered;
  }, [products, searchQuery, selectedCategory, selectedBrand, sortBy, showInStockOnly]);

  // Group products by category
  const productsByCategory = useMemo(() => {
    const grouped: Record<string, Product[]> = {};
    filteredProducts.forEach(product => {
      const categoryName = product.category?.name || 'Uncategorized';
      if (!grouped[categoryName]) {
        grouped[categoryName] = [];
      }
      grouped[categoryName].push(product);
    });
    return grouped;
  }, [filteredProducts]);

  const scrollToTop = () => {
    window.scrollTo({ top: 0, behavior: 'smooth' });
  };

  const scrollToCategory = (categorySlug: string) => {
    const element = document.getElementById(`category-${categorySlug}`);
    if (element) {
      element.scrollIntoView({ behavior: 'smooth' });
    }
  };

  if (productsLoading) {
    return (
      <div className="min-h-screen bg-gray-50">
        <Header />
        <div className="max-w-7xl mx-auto px-4 py-8">
          <div className="animate-pulse">
            <div className="h-8 bg-gray-200 rounded w-1/3 mb-8"></div>
            <div className="grid grid-cols-1 md:grid-cols-3 lg:grid-cols-4 gap-6">
              {[...Array(12)].map((_, i) => (
                <div key={i} className="bg-white rounded-lg p-4">
                  <div className="h-48 bg-gray-200 rounded mb-4"></div>
                  <div className="h-4 bg-gray-200 rounded w-3/4 mb-2"></div>
                  <div className="h-4 bg-gray-200 rounded w-1/2"></div>
                </div>
              ))}
            </div>
          </div>
        </div>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-gray-50">
      <Header />
      <div className="bg-white border-b sticky top-16 z-40">
        <div className="max-w-7xl mx-auto px-4 py-4">
          <div className="flex flex-col lg:flex-row gap-4 items-center justify-between">
            <div className="flex items-center gap-4">
              <h1 className="text-2xl font-bold text-blue-900">
                Browse All Products
              </h1>
              <Badge variant="secondary">
                {filteredProducts.length} products
              </Badge>
            </div>

            <div className="flex flex-col sm:flex-row gap-4 w-full lg:w-auto">
              {/* Search */}
              <div className="relative">
                <Search className="absolute left-3 top-1/2 transform -y-1/2 h-4 w-4 text-gray-400" />
                <Input
                  placeholder="Search products or brands..."
                  value={searchQuery}
                  onChange={(e) => setSearchQuery(e.target.value)}
                  className="pl-10 w-full sm:w-80"
                />
              </div>

              {/* View mode toggle */}
              <div className="flex gap-2">
                <Button
                  variant={viewMode === 'grid' ? 'default' : 'outline'}
                  size="sm"
                  onClick={() => setViewMode('grid')}
                >
                  <Grid className="h-4 w-4" />
                </Button>
                <Button
                  variant={viewMode === 'list' ? 'default' : 'outline'}
                  size="sm"
                  onClick={() => setViewMode('list')}
                >
                  <List className="h-4 w-4" />
                </Button>
              </div>
            </div>
          </div>
        </div>
      </div>

      <div className="max-w-7xl mx-auto px-4 py-8">
        <div className="flex flex-col lg:flex-row gap-8">
          {/* Sidebar Filters */}
          <aside className="lg:w-64 space-y-6">
            <div className="bg-white rounded-lg p-6 shadow-sm">
              <h2 className="font-semibold text-gray-900 mb-4">Refine your search</h2>

              {/* Categories */}
              <div className="mb-6">
                <h3 className="font-medium text-gray-700 mb-3">Categories</h3>
                <div className="space-y-2 max-h-64 overflow-y-auto">
                  <button
                    onClick={() => setSelectedCategory('all')}
                    className={`block w-full text-left px-3 py-2 rounded text-sm transition-colors ${
                      selectedCategory === 'all'
                        ? 'bg-blue-50 text-blue-700'
                        : 'text-gray-600 hover:bg-gray-50'
                    }`}
                  >
                    All Categories
                  </button>
                  {categories.map((category) => (
                    <button
                      key={category.id}
                      onClick={() => setSelectedCategory(category.slug)}
                      className={`block w-full text-left px-3 py-2 rounded text-sm transition-colors ${
                        selectedCategory === category.slug
                          ? 'bg-blue-50 text-blue-700'
                          : 'text-gray-600 hover:bg-gray-50'
                      }`}
                    >
                      {category.name}
                    </button>
                  ))}
                </div>
              </div>

              {/* Quick category links */}
              <div className="mb-6">
                <h3 className="font-medium text-gray-700 mb-3">Quick Links</h3>
                <div className="space-y-1">
                  {Object.keys(productsByCategory).slice(0, 8).map((categoryName) => {
                    const category = categories.find(c => c.name === categoryName);
                    return (
                      <button
                        key={categoryName}
                        onClick={() => category && scrollToCategory(category.slug)}
                        className="block text-sm text-blue-600 hover:text-blue-800 hover:underline"
                      >
                        {categoryName}
                      </button>
                    );
                  })}
                </div>
              </div>

              {/* Brands */}
              <div className="mb-6">
                <h3 className="font-medium text-gray-700 mb-3">Brands</h3>
                <Select value={selectedBrand} onValueChange={setSelectedBrand}>
                  <SelectTrigger className="w-full">
                    <SelectValue placeholder="All Brands" />
                  </SelectTrigger>
                  <SelectContent>
                    <SelectItem value="all">All Brands</SelectItem>
                    {brands.map((brand) => (
                      <SelectItem key={brand.id} value={brand.name}>
                        {brand.name}
                      </SelectItem>
                    ))}
                  </SelectContent>
                </Select>
              </div>

              {/* Availability */}
              <div>
                <h3 className="font-medium text-gray-700 mb-3">Availability</h3>
                <label className="flex items-center space-x-2">
                  <input
                    type="checkbox"
                    checked={showInStockOnly}
                    onChange={(e) => setShowInStockOnly(e.target.checked)}
                    className="rounded border-gray-300"
                  />
                  <span className="text-sm text-gray-600">In stock only</span>
                </label>
              </div>
            </div>
          </aside>

          {/* Main Content */}
          <main className="flex-1">
            {/* Sort Controls */}
            <div className="bg-white rounded-lg p-4 mb-6 shadow-sm">
              <div className="flex flex-col sm:flex-row justify-between items-start sm:items-center gap-4">
                <div className="text-sm text-gray-600">
                  Showing {filteredProducts.length} products
                </div>
                <div className="flex items-center gap-4">
                  <div className="flex items-center gap-2">
                    <span className="text-sm text-gray-600">Sort by:</span>
                    <Select value={sortBy} onValueChange={(value: SortOption) => setSortBy(value)}>
                      <SelectTrigger className="w-40">
                        <SelectValue />
                      </SelectTrigger>
                      <SelectContent>
                        <SelectItem value="relevant">Most Relevant</SelectItem>
                        <SelectItem value="name">A - Z</SelectItem>
                        <SelectItem value="rating">Best Rating</SelectItem>
                        <SelectItem value="price-high">Price High - Low</SelectItem>
                        <SelectItem value="price-low">Price Low - High</SelectItem>
                      </SelectContent>
                    </Select>
                  </div>
                </div>
              </div>
            </div>

            {/* Products by Category */}
            <div className="space-y-8">
              {Object.entries(productsByCategory).map(([categoryName, products]) => {
                const category = categories.find(c => c.name === categoryName);
                return (
                  <div key={categoryName} id={`category-${category?.slug || 'uncategorized'}`}>
                    <div className="mb-6">
                      <h2 className="text-2xl font-bold text-gray-900 mb-2">{categoryName}</h2>
                      <div className="h-1 w-20 bg-blue-600 rounded"></div>
                    </div>

                    <div className={`grid gap-6 ${
                      viewMode === 'grid' 
                        ? 'grid-cols-1 md:grid-cols-2 lg:grid-cols-3 xl:grid-cols-4'
                        : 'grid-cols-1'
                    }`}>
                      {products.map((product) => (
                        <ProductCard
                          key={product.id}
                          product={product}
                          viewMode={viewMode}
                        />
                      ))}
                    </div>
                  </div>
                );
              })}
            </div>

            {filteredProducts.length === 0 && (
              <div className="bg-white rounded-lg p-12 text-center shadow-sm">
                <div className="text-gray-400 text-6xl mb-4">📦</div>
                <h3 className="text-xl font-semibold text-gray-900 mb-2">No products found</h3>
                <p className="text-gray-600 mb-4">
                  Try adjusting your filters or search terms
                </p>
                <Button onClick={() => {
                  setSearchQuery("");
                  setSelectedCategory("all");
                  setSelectedBrand("all");
                  setShowInStockOnly(false);
                }}>
                  Clear all filters
                </Button>
              </div>
            )}
          </main>
        </div>
      </div>

      {/* Back to Top Button */}
      {showBackToTop && (
        <Button
          onClick={scrollToTop}
          className="fixed bottom-6 right-6 z-50 rounded-full w-12 h-12 p-0"
          size="sm"
        >
          <ChevronUp className="h-5 w-5" />
        </Button>
      )}
    </div>
  );
}

interface ProductCardProps {
  product: Product;
  viewMode: ViewMode;
}

function ProductCard({ product, viewMode }: ProductCardProps) {
  const isOutOfStock = product.stockQuantity === 0;
  const hasDiscount = product.originalPrice && parseFloat(product.originalPrice) > parseFloat(product.price);

  if (viewMode === 'list') {
    return (
      <Card className="hover:shadow-md transition-shadow">
        <CardContent className="p-6">
          <div className="flex gap-6">
            <div className="flex-shrink-0">
              <div className="w-32 h-32 bg-gray-100 rounded-lg overflow-hidden relative">
                <img
                  src={product.imageUrl}
                  alt={product.name}
                  className="w-full h-full object-cover"
                  onError={(e) => {
                    const img = e.target as HTMLImageElement;
                    img.src = "https://images.unsplash.com/photo-1584308666744-24d5c474f2ae?auto=format&fit=crop&w=400&h=300";
                  }}
                />
                {isOutOfStock && (
                  <div className="absolute inset-0 bg-black bg-opacity-50 flex items-center justify-center">
                    <span className="text-white text-sm font-medium">Out of Stock</span>
                  </div>
                )}
                {hasDiscount && (
                  <Badge className="absolute top-2 left-2 bg-red-500">
                    Sale
                  </Badge>
                )}
              </div>
            </div>
            <div className="flex-1 min-w-0">
              <div className="flex justify-between items-start mb-2">
                <div className="flex-1">
                  {!isOutOfStock && (
                    <Badge variant="secondary" className="mb-2 bg-green-100 text-green-800">
                      In stock
                    </Badge>
                  )}
                  <Link
                    href={`/product/${product.slug}`}
                    className="block font-semibold text-gray-900 hover:text-blue-600 transition-colors mb-2"
                  >
                    {product.name}
                  </Link>
                  {product.brand && (
                    <p className="text-sm text-gray-500 mb-2">{product.brand.name}</p>
                  )}
                  {product.shortDescription && (
                    <p className="text-sm text-gray-600 line-clamp-2">{product.shortDescription}</p>
                  )}
                </div>
                <div className="text-right ml-4">
                  <div className="flex flex-col items-end">
                    <span className="text-sm text-gray-500">From</span>
                    <div className="flex items-center gap-2">
                      <span className="text-2xl font-bold text-gray-900">₵{product.price}</span>
                      {hasDiscount && (
                        <span className="text-lg text-gray-500 line-through">
                          ₵{product.originalPrice}
                        </span>
                      )}
                    </div>
                  </div>
                </div>
              </div>
              <div className="flex items-center gap-4 mt-4">
                <div className="flex items-center gap-1">
                  <div className="flex">
                    {[...Array(5)].map((_, i) => (
                      <span
                        key={i}
                        className={`text-xs ${
                          i < Math.floor(parseFloat(product.rating))
                            ? "text-yellow-400"
                            : "text-gray-300"
                        }`}
                      >
                        ★
                      </span>
                    ))}
                  </div>
                  <span className="text-sm text-gray-500">
                    ({product.reviewCount} reviews)
                  </span>
                </div>
                {product.requiresPrescription && (
                  <Badge variant="outline" className="text-xs">
                    Prescription Required
                  </Badge>
                )}
              </div>
            </div>
          </div>
        </CardContent>
      </Card>
    );
  }

  return (
    <Card className="hover:shadow-md transition-shadow group">
      <CardContent className="p-0">
        <div className="aspect-square bg-gray-100 relative overflow-hidden rounded-t-lg">
          <img
            src={product.imageUrl}
            alt={product.name}
            className="w-full h-full object-cover group-hover:scale-105 transition-transform duration-300"
            onError={(e) => {
              const img = e.target as HTMLImageElement;
              img.src = "https://images.unsplash.com/photo-1584308666744-24d5c474f2ae?auto=format&fit=crop&w=400&h=300";
            }}
          />
          {isOutOfStock && (
            <div className="absolute inset-0 bg-black bg-opacity-50 flex items-center justify-center">
              <span className="text-white font-medium">Out of Stock</span>
            </div>
          )}
          {hasDiscount && (
            <Badge className="absolute top-2 right-2 bg-red-500">
              Sale
            </Badge>
          )}
          {!isOutOfStock && (
            <Badge variant="secondary" className="absolute top-2 left-2 bg-green-100 text-green-800">
              In stock
            </Badge>
          )}
        </div>
        <div className="p-4">
          <Link
            href={`/product/${product.slug}`}
            className="block font-semibold text-gray-900 hover:text-blue-600 transition-colors mb-2 line-clamp-2"
          >
            {product.name}
          </Link>
          {product.brand && (
            <p className="text-sm text-gray-500 mb-3">{product.brand.name}</p>
          )}
          <div className="flex items-center gap-1 mb-3">
            <div className="flex">
              {[...Array(5)].map((_, i) => (
                <span
                  key={i}
                  className={`text-xs ${
                    i < Math.floor(parseFloat(product.rating))
                      ? "text-yellow-400"
                      : "text-gray-300"
                  }`}
                >
                  ★
                </span>
              ))}
            </div>
            <span className="text-xs text-gray-500">
              ({product.reviewCount})
            </span>
          </div>
          <div className="flex flex-col">
            <span className="text-xs text-gray-500 mb-1">From</span>
            <div className="flex items-center gap-2">
              <span className="text-xl font-bold text-gray-900">₵{product.price}</span>
              {hasDiscount && (
                <span className="text-sm text-gray-500 line-through">
                  ₵{product.originalPrice}
                </span>
              )}
            </div>
          </div>
          {product.requiresPrescription && (
            <Badge variant="outline" className="text-xs mt-2">
              Prescription Required
            </Badge>
          )}
        </div>
      </CardContent>
    </Card>
  );
}

