import { useState, useRef } from "react";
import Header from "@/components/layout/header";
import { useForm } from "react-hook-form";
import { zodResolver } from "@hookform/resolvers/zod";
import { z } from "zod";
import { format } from "date-fns";
import { Calendar as CalendarIcon, Upload, Camera, FileText, Shield, MessageCircle, HelpCircle, CheckCircle2, X, Eye } from "lucide-react";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Textarea } from "@/components/ui/textarea";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Checkbox } from "@/components/ui/checkbox";
import { Calendar } from "@/components/ui/calendar";
import { Popover, PopoverContent, PopoverTrigger } from "@/components/ui/popover";
import { Form, FormControl, FormField, FormItem, FormLabel, FormMessage, FormDescription } from "@/components/ui/form";
import { useToast } from "@/hooks/use-toast";
import { Badge } from "@/components/ui/badge";
import { Separator } from "@/components/ui/separator";
import { useMutation, useQuery } from "@tanstack/react-query";
import { apiRequest } from "@/lib/queryClient";
import { useAuth } from "@/hooks/useAuth";

const prescriptionSchema = z.object({
  patientName: z.string().min(2, "Patient name must be at least 2 characters"),
  doctorName: z.string().min(2, "Doctor name must be at least 2 characters"),
  doctorContact: z.string().min(10, "Please provide valid doctor contact"),
  prescriptionDate: z.date({
    required_error: "Prescription date is required",
  }),
  medications: z.string().optional(),
  privacyConsent: z.boolean().refine(val => val === true, {
    message: "You must consent to prescription verification",
  }),
});

type PrescriptionFormData = z.infer<typeof prescriptionSchema>;

interface UploadedFile {
  file: File;
  preview: string;
  id: string;
}

interface PrescriptionHistory {
  id: string;
  patientName: string;
  doctorName: string;
  prescriptionDate: string;
  status: 'pending' | 'verified' | 'rejected';
  createdAt: string;
  files: string[];
}

export default function PrescriptionPage() {
  const { toast } = useToast();
  const { user, isAuthenticated } = useAuth();
  const [uploadedFiles, setUploadedFiles] = useState<UploadedFile[]>([]);
  const [isDragOver, setIsDragOver] = useState(false);
  const [showHistory, setShowHistory] = useState(false);
  const [showFAQ, setShowFAQ] = useState(false);
  const fileInputRef = useRef<HTMLInputElement>(null);

  const form = useForm<PrescriptionFormData>({
    resolver: zodResolver(prescriptionSchema),
    defaultValues: {
      patientName: "",
      doctorName: "",
      doctorContact: "",
      medications: "",
      privacyConsent: false,
    },
  });

  // Fetch prescription history
  const { data: prescriptionHistory } = useQuery<PrescriptionHistory[]>({
    queryKey: ['/api/prescriptions/history'],
    enabled: isAuthenticated,
  });

  // Submit prescription mutation
  const submitPrescription = useMutation({
    mutationFn: async (data: PrescriptionFormData & { files: File[] }) => {
      const formData = new FormData();

      // Add form fields
      Object.entries(data).forEach(([key, value]) => {
        if (key !== 'files' && key !== 'prescriptionDate') {
          formData.append(key, value.toString());
        }
      });

      formData.append('prescriptionDate', data.prescriptionDate.toISOString());

      // Add files
      data.files.forEach((file, index) => {
        formData.append(`prescription_${index}`, file);
      });

      return apiRequest("POST", "/api/prescriptions/submit", formData);
    },
    onSuccess: (response: any) => {
      toast({
        title: "Prescription Submitted Successfully",
        description: "Your prescription has been sent for verification. You'll receive a WhatsApp confirmation shortly.",
      });

      // Reset form and files
      form.reset();
      setUploadedFiles([]);

      // Send WhatsApp message
      const whatsappData = form.getValues();
      const message = generateWhatsAppMessage(whatsappData, response.id);
      window.open(`https://wa.me/233200751811?text=${encodeURIComponent(message)}`, '_blank');
    },
    onError: (error) => {
      toast({
        title: "Submission Failed",
        description: error.message || "Failed to submit prescription. Please try again.",
        variant: "destructive",
      });
    },
  });

  const generateWhatsAppMessage = (data: PrescriptionFormData, prescriptionId: string) => {
    const baseUrl = window.location.origin;
    return `🏥 *New Prescription Submission*

*Patient Details:*
• Name: ${data.patientName}
• Prescription Date: ${format(data.prescriptionDate, 'PPP')}

*Doctor Information:*
• Doctor: ${data.doctorName}
• Contact: ${data.doctorContact}

*Medications:* ${data.medications || 'Not specified'}

*Prescription ID:* ${prescriptionId}

📋 *View Full Details & Images:*
${baseUrl}/prescription-view/${prescriptionId}

Please verify this prescription and confirm medication availability.`;
  };

  const handleFileSelect = (files: File[]) => {
    const validFiles = files.filter(file => {
      const isValidType = ['image/jpeg', 'image/jpg', 'image/png', 'application/pdf'].includes(file.type);
      const isValidSize = file.size <= 5 * 1024 * 1024; // 5MB

      if (!isValidType) {
        toast({
          title: "Invalid File Type",
          description: "Please upload only JPG, PNG, or PDF files.",
          variant: "destructive",
        });
        return false;
      }

      if (!isValidSize) {
        toast({
          title: "File Too Large",
          description: "Files must be under 5MB.",
          variant: "destructive",
        });
        return false;
      }

      return true;
    });

    const newFiles: UploadedFile[] = validFiles.map(file => ({
      file,
      preview: file.type.startsWith('image/') ? URL.createObjectURL(file) : '',
      id: Math.random().toString(36).substring(7),
    }));

    setUploadedFiles(prev => [...prev, ...newFiles]);
  };

  const handleDrop = (e: React.DragEvent) => {
    e.preventDefault();
    setIsDragOver(false);
    const files = Array.from(e.dataTransfer.files);
    handleFileSelect(files);
  };

  const handleFileInputChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    if (e.target.files) {
      const files = Array.from(e.target.files);
      handleFileSelect(files);
    }
  };

  const handleCameraCapture = () => {
    if (fileInputRef.current) {
      fileInputRef.current.setAttribute('capture', 'camera');
      fileInputRef.current.click();
    }
  };

  const removeFile = (fileId: string) => {
    setUploadedFiles(prev => prev.filter(f => f.id !== fileId));
  };

  const onSubmit = (data: PrescriptionFormData) => {
    if (uploadedFiles.length === 0) {
      toast({
        title: "No Prescription Uploaded",
        description: "Please upload at least one prescription image or PDF.",
        variant: "destructive",
      });
      return;
    }

    const files = uploadedFiles.map(f => f.file);
    submitPrescription.mutate({ ...data, files });
  };

  const faqItems = [
    {
      question: "What file formats are accepted?",
      answer: "We accept JPG, PNG, and PDF files up to 5MB each. Make sure your prescription is clear and readable."
    },
    {
      question: "How long does verification take?",
      answer: "Prescription verification typically takes 2-4 hours during business hours (9 AM - 6 PM, Monday-Saturday)."
    },
    {
      question: "What if my prescription is rejected?",
      answer: "If rejected, we'll contact you via WhatsApp with the reason and guide you on resubmission requirements."
    },
    {
      question: "Is my prescription information secure?",
      answer: "Yes, all prescription data is encrypted and only used for verification. We comply with healthcare privacy standards."
    },
    {
      question: "Can I order without a prescription?",
      answer: "Over-the-counter medications don't require prescriptions, but prescription drugs legally require valid prescriptions."
    }
  ];

  return (
    <div className="min-h-screen bg-gray-50">
      {/* Header */}
      <Header />

      <div className="py-8">
        <div className="max-w-4xl mx-auto px-4">
        {/* Header */}
        <div className="text-center mb-8">
          <h1 className="text-3xl font-bold text-neutral mb-4">Upload Your Prescription</h1>
          <p className="text-gray-600 max-w-2xl mx-auto">
            Prescriptions are required for certain medications to ensure your safety and legal compliance. 
            Our certified pharmacy technicians will verify your prescription within 2-4 hours.
          </p>
        </div>

        {/* Quick Actions */}
        {isAuthenticated && (
          <div className="flex justify-center gap-4 mb-8">
            <Button
              variant="outline"
              onClick={() => setShowHistory(!showHistory)}
              className="flex items-center gap-2"
            >
              <FileText className="h-4 w-4" />
              Prescription History
            </Button>
            <Button
              variant="outline"
              onClick={() => setShowFAQ(!showFAQ)}
              className="flex items-center gap-2"
            >
              <HelpCircle className="h-4 w-4" />
              FAQ
            </Button>
          </div>
        )}

        {/* Prescription History */}
        {showHistory && prescriptionHistory && (
          <Card className="mb-8">
            <CardHeader>
              <CardTitle className="flex items-center gap-2">
                <FileText className="h-5 w-5" />
                Your Prescription History
              </CardTitle>
            </CardHeader>
            <CardContent>
              {prescriptionHistory.length === 0 ? (
                <p className="text-gray-500 text-center py-4">No prescriptions submitted yet.</p>
              ) : (
                <div className="space-y-4">
                  {prescriptionHistory.map((prescription) => (
                    <div key={prescription.id} className="flex items-center justify-between p-4 border rounded-lg">
                      <div className="flex-1">
                        <h4 className="font-medium">{prescription.patientName}</h4>
                        <p className="text-sm text-gray-600">Dr. {prescription.doctorName}</p>
                        <p className="text-sm text-gray-500">
                          Submitted: {format(new Date(prescription.createdAt), 'PPP')}
                        </p>
                      </div>
                      <div className="flex items-center gap-2">
                        <Badge 
                          variant={
                            prescription.status === 'verified' ? 'default' :
                            prescription.status === 'rejected' ? 'destructive' : 'secondary'
                          }
                        >
                          {prescription.status}
                        </Badge>
                        <Button variant="ghost" size="sm">
                          <Eye className="h-4 w-4" />
                        </Button>
                      </div>
                    </div>
                  ))}
                </div>
              )}
            </CardContent>
          </Card>
        )}

        {/* FAQ Section */}
        {showFAQ && (
          <Card className="mb-8">
            <CardHeader>
              <CardTitle className="flex items-center gap-2">
                <HelpCircle className="h-5 w-5" />
                Frequently Asked Questions
              </CardTitle>
            </CardHeader>
            <CardContent>
              <div className="space-y-6">
                {faqItems.map((item, index) => (
                  <div key={index}>
                    <h4 className="font-medium text-neutral mb-2">{item.question}</h4>
                    <p className="text-gray-600 text-sm">{item.answer}</p>
                    {index < faqItems.length - 1 && <Separator className="mt-4" />}
                  </div>
                ))}
              </div>
            </CardContent>
          </Card>
        )}

        <div className="grid lg:grid-cols-2 gap-8">
          {/* Upload Section */}
          <Card>
            <CardHeader>
              <CardTitle className="flex items-center gap-2">
                <Upload className="h-5 w-5" />
                Upload Prescription
              </CardTitle>
              <p className="text-sm text-gray-600">
                Accepted formats: PDF, JPG, PNG • Max size: 5MB per file
              </p>
            </CardHeader>
            <CardContent className="space-y-6">
              {/* Drag & Drop Area */}
              <div
                className={`border-2 border-dashed rounded-lg p-8 text-center transition-colors ${
                  isDragOver ? 'border-primary bg-primary/5' : 'border-gray-300'
                }`}
                onDragOver={(e) => {
                  e.preventDefault();
                  setIsDragOver(true);
                }}
                onDragLeave={() => setIsDragOver(false)}
                onDrop={handleDrop}
              >
                <Upload className="h-12 w-12 text-gray-400 mx-auto mb-4" />
                <h3 className="text-lg font-medium mb-2">Drop your prescription here</h3>
                <p className="text-gray-600 mb-4">or choose files to upload</p>

                <div className="flex gap-3 justify-center">
                  <Button
                    type="button"
                    variant="outline"
                    onClick={() => fileInputRef.current?.click()}
                  >
                    <FileText className="h-4 w-4 mr-2" />
                    Browse Files
                  </Button>

                  <Button
                    type="button"
                    variant="outline"
                    onClick={handleCameraCapture}
                    className="md:hidden"
                  >
                    <Camera className="h-4 w-4 mr-2" />
                    Take Photo
                  </Button>
                </div>

                <input
                  ref={fileInputRef}
                  type="file"
                  accept=".jpg,.jpeg,.png,.pdf"
                  multiple
                  onChange={handleFileInputChange}
                  className="hidden"
                />
              </div>

              {/* Uploaded Files */}
              {uploadedFiles.length > 0 && (
                <div className="space-y-3">
                  <h4 className="font-medium">Uploaded Files ({uploadedFiles.length})</h4>
                  {uploadedFiles.map((uploadedFile) => (
                    <div key={uploadedFile.id} className="flex items-center justify-between p-3 bg-gray-50 rounded-lg">
                      <div className="flex items-center gap-3">
                        {uploadedFile.preview ? (
                          <img src={uploadedFile.preview} alt="Preview" className="h-10 w-10 object-cover rounded" />
                        ) : (
                          <FileText className="h-10 w-10 text-gray-400" />
                        )}
                        <div>
                          <p className="text-sm font-medium">{uploadedFile.file.name}</p>
                          <p className="text-xs text-gray-500">
                            {(uploadedFile.file.size / 1024 / 1024).toFixed(2)} MB
                          </p>
                        </div>
                      </div>
                      <Button
                        type="button"
                        variant="ghost"
                        size="sm"
                        onClick={() => removeFile(uploadedFile.id)}
                      >
                        <X className="h-4 w-4" />
                      </Button>
                    </div>
                  ))}
                </div>
              )}
            </CardContent>
          </Card>

          {/* Form Section */}
          <Card>
            <CardHeader>
              <CardTitle>Prescription Details</CardTitle>
            </CardHeader>
            <CardContent>
              <Form {...form}>
                <form onSubmit={form.handleSubmit(onSubmit)} className="space-y-6">
                  <FormField
                    control={form.control}
                    name="patientName"
                    render={({ field }) => (
                      <FormItem>
                        <FormLabel>Patient Name</FormLabel>
                        <FormControl>
                          <Input placeholder="Enter patient's full name" {...field} />
                        </FormControl>
                        <FormMessage />
                      </FormItem>
                    )}
                  />

                  <FormField
                    control={form.control}
                    name="doctorName"
                    render={({ field }) => (
                      <FormItem>
                        <FormLabel>Doctor's Name</FormLabel>
                        <FormControl>
                          <Input placeholder="Enter prescribing doctor's name" {...field} />
                        </FormControl>
                        <FormMessage />
                      </FormItem>
                    )}
                  />

                  <FormField
                    control={form.control}
                    name="doctorContact"
                    render={({ field }) => (
                      <FormItem>
                        <FormLabel>Doctor's Contact</FormLabel>
                        <FormControl>
                          <Input placeholder="Phone number or hospital contact" {...field} />
                        </FormControl>
                        <FormMessage />
                      </FormItem>
                    )}
                  />

                  <FormField
                    control={form.control}
                    name="prescriptionDate"
                    render={({ field }) => (
                      <FormItem className="flex flex-col">
                        <FormLabel>Prescription Date</FormLabel>
                        <Popover>
                          <PopoverTrigger asChild>
                            <FormControl>
                              <Button
                                variant="outline"
                                className={`w-full pl-3 text-left font-normal ${!field.value && "text-muted-foreground"}`}
                              >
                                {field.value ? (
                                  format(field.value, "PPP")
                                ) : (
                                  <span>Select prescription date</span>
                                )}
                                <CalendarIcon className="ml-auto h-4 w-4 opacity-50" />
                              </Button>
                            </FormControl>
                          </PopoverTrigger>
                          <PopoverContent className="w-auto p-0" align="start">
                            <Calendar
                              mode="single"
                              selected={field.value}
                              onSelect={field.onChange}
                              disabled={(date) =>
                                date > new Date() || date < new Date("1900-01-01")
                              }
                              initialFocus
                            />
                          </PopoverContent>
                        </Popover>
                        <FormMessage />
                      </FormItem>
                    )}
                  />

                  <FormField
                    control={form.control}
                    name="medications"
                    render={({ field }) => (
                      <FormItem>
                        <FormLabel>Medications & Dosage (Optional)</FormLabel>
                        <FormControl>
                          <Textarea
                            placeholder="List medications and dosages if visible on prescription"
                            className="resize-none"
                            {...field}
                          />
                        </FormControl>
                        <FormDescription>
                          This helps us prepare your order faster
                        </FormDescription>
                        <FormMessage />
                      </FormItem>
                    )}
                  />

                  {/* Privacy Section */}
                  <div className="border-t pt-6">
                    <div className="flex items-start gap-3 p-4 bg-blue-50 rounded-lg mb-4">
                      <Shield className="h-5 w-5 text-blue-600 mt-0.5 flex-shrink-0" />
                      <div className="text-sm">
                        <h4 className="font-medium text-blue-900 mb-1">Privacy & Security</h4>
                        <p className="text-blue-800">
                          Your prescription will only be used for order verification and will remain confidential. 
                          We comply with healthcare privacy standards and never share your medical information.
                        </p>
                      </div>
                    </div>

                    <FormField
                      control={form.control}
                      name="privacyConsent"
                      render={({ field }) => (
                        <FormItem className="flex flex-row items-start space-x-3 space-y-0">
                          <FormControl>
                            <Checkbox
                              checked={field.value}
                              onCheckedChange={field.onChange}
                            />
                          </FormControl>
                          <div className="space-y-1 leading-none">
                            <FormLabel className="text-sm font-normal">
                              I consent to storing and verifying my prescription for order processing
                            </FormLabel>
                            <FormMessage />
                          </div>
                        </FormItem>
                      )}
                    />
                  </div>

                  <Button
                    type="submit"
                    className="w-full bg-primary hover:bg-primary/90"
                    disabled={submitPrescription.isPending}
                  >
                    {submitPrescription.isPending ? (
                      "Submitting..."
                    ) : (
                      <>
                        <CheckCircle2 className="h-4 w-4 mr-2" />
                        Submit Prescription
                      </>
                    )}
                  </Button>
                </form>
              </Form>
            </CardContent>
          </Card>
        </div>

        {/* Support Section */}
        <Card className="mt-8">
          <CardContent className="p-6">
            <div className="text-center">
              <MessageCircle className="h-8 w-8 text-secondary mx-auto mb-3" />
              <h3 className="text-lg font-semibold mb-2">Need Help with Your Prescription?</h3>
              <p className="text-gray-600 mb-4">
                Our certified pharmacy technicians are available to assist you with prescription requirements
              </p>
              <div className="flex gap-3 justify-center">
                <a href="https://wa.me/233200751811" target="_blank">
                  <Button className="bg-secondary hover:bg-secondary/90">
                    <MessageCircle className="h-4 w-4 mr-2" />
                    WhatsApp Support
                  </Button>
                </a>
                <Button variant="outline">
                  <HelpCircle className="h-4 w-4 mr-2" />
                  Live Chat
                </Button>
              </div>
            </div>
          </CardContent>
        </Card>
        </div>
      </div>
    </div>
  );
}

client/src/pages/product-detail.tsx.content:
import { useState } from "react";
import { useRoute } from "wouter";
import { useQuery, useMutation, useQueryClient } from "@tanstack/react-query";
import { useAuth } from "@/hooks/useAuth";
import { type Product } from "@shared/schema";
import Header from "@/components/layout/header";
import Footer from "@/components/layout/footer";
import { Button } from "@/components/ui/button";
import { Badge } from "@/components/ui/badge";
import { Card, CardContent } from "@/components/ui/card";
import { Separator } from "@/components/ui/separator";
import { useToast } from "@/hooks/use-toast";
import { apiRequest } from "@/lib/queryClient";
import { isUnauthorizedError } from "@/lib/authUtils";
import { 
  ShoppingCart, 
  Heart, 
  Star, 
  Shield, 
  Truck, 
  RotateCcw,
  Plus,
  Minus,
  AlertCircle,
  CheckCircle
} from "lucide-react";

export default function ProductDetail() {
  const [, params] = useRoute("/product/:slug");
  const { isAuthenticated } = useAuth();
  const { toast } = useToast();
  const queryClient = useQueryClient();
  const [quantity, setQuantity] = useState(1);

  const { data: product, isLoading: productLoading } = useQuery<Product>({
    queryKey: [`/api/products/slug/${params?.slug}`],
    enabled: !!params?.slug,
  });

  const { data: relatedProducts } = useQuery<Product[]>({
    queryKey: [`/api/products?categoryId=${product?.categoryId}&limit=4`],
    enabled: !!product?.categoryId,
  });

  const addToCartMutation = useMutation({
    mutationFn: async (data: { productId: string; quantity: number }) => {
      const response = await apiRequest("POST", "/api/cart", data);
      return response.json();
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/cart"] });
      toast({
        title: "Added to Cart",
        description: `${product?.name} has been added to your cart.`,
      });
    },
    onError: (error) => {
      if (isUnauthorizedError(error)) {
        toast({
          title: "Please Login",
          description: "You need to be logged in to add items to cart.",
          variant: "destructive",
        });
        setTimeout(() => {
          window.location.href = "/api/login";
        }, 1000);
        return;
      }
      toast({
        title: "Error",
        description: "Failed to add item to cart. Please try again.",
        variant: "destructive",
      });
    },
  });

  const handleAddToCart = () => {
    if (!isAuthenticated) {
      toast({
        title: "Please Login",
        description: "You need to be logged in to add items to cart.",
        variant: "destructive",
      });
      setTimeout(() => {
        window.location.href = "/api/login";
      }, 1000);
      return;
    }

    if (product) {
      addToCartMutation.mutate({
        productId: product.id,
        quantity,
      });
    }
  };

  const adjustQuantity = (delta: number) => {
    const newQuantity = quantity + delta;
    if (newQuantity >= 1 && newQuantity <= (product?.stockQuantity || 1)) {
      setQuantity(newQuantity);
    }
  };

  if (productLoading) {
    return (
      <div className="min-h-screen bg-gray-50">
        <Header />
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-12">
          <div className="animate-pulse">
            <div className="grid md:grid-cols-2 gap-12">
              <div className="bg-gray-200 rounded-lg h-96"></div>
              <div className="space-y-4">
                <div className="h-8 bg-gray-200 rounded w-3/4"></div>
                <div className="h-4 bg-gray-200 rounded w-1/2"></div>
                <div className="h-6 bg-gray-200 rounded w-1/4"></div>
                <div className="h-24 bg-gray-200 rounded"></div>
              </div>
            </div>
          </div>
        </div>
      </div>
    );
  }

  if (!product) {
    return (
      <div className="min-h-screen bg-gray-50">
        <Header />
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-12">
          <div className="text-center">
            <AlertCircle className="h-16 w-16 text-gray-400 mx-auto mb-4" />
            <h1 className="text-2xl font-bold text-gray-900 mb-2">Product Not Found</h1>
            <p className="text-gray-600">The product you're looking for doesn't exist.</p>
          </div>
        </div>
      </div>
    );
  }

  const isInStock = product.stockQuantity > 0;
  const rating = parseFloat(product.rating || "0");

  return (
    <div className="min-h-screen bg-gray-50">
      <Header />

      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-12">
        {/* Product Details */}
        <div className="grid md:grid-cols-2 gap-12 mb-16">
          {/* Product Image */}
          <div className="relative">
            <img
              src={product.imageUrl || "https://images.unsplash.com/photo-1584308666744-24d5c474f2ae?auto=format&fit=crop&w=600&h=600"}
              alt={product.name}
              className="w-full h-96 object-cover rounded-lg shadow-lg"
            />
            <button className="absolute top-4 right-4 bg-white rounded-full p-3 shadow-md hover:bg-gray-50 transition-colors">
              <Heart className="h-5 w-5 text-gray-400 hover:text-red-500" />
            </button>
            {!isInStock && (
              <div className="absolute inset-0 bg-black/50 rounded-lg flex items-center justify-center">
                <Badge variant="destructive" className="text-lg px-4 py-2">
                  Out of Stock
                </Badge>
              </div>
            )}
          </div>

          {/* Product Information */}
          <div className="space-y-6">
            <div>
              <div className="flex items-center space-x-2 mb-2">
                <Badge 
                  variant={product.requiresPrescription ? "destructive" : "secondary"}
                  className="text-xs"
                >
                  {product.requiresPrescription ? "Prescription Required" : "OTC"}
                </Badge>
                {isInStock ? (
                  <Badge variant="default" className="bg-secondary text-white">
                    <CheckCircle className="h-3 w-3 mr-1" />
                    In Stock
                  </Badge>
                ) : (
                  <Badge variant="destructive">
                    Out of Stock
                  </Badge>
                )}
              </div>

              <h1 className="text-3xl font-bold text-neutral mb-2">{product.name}</h1>
              <p className="text-lg text-gray-600 mb-4">{product.brand?.name}</p>

              {/* Rating */}
              <div className="flex items-center space-x-2 mb-4">
                <div className="flex">
                  {[...Array(5)].map((_, i) => (
                    <Star
                      key={i}
                      className={`h-4 w-4 ${
                        i < Math.floor(rating)
                          ? "text-yellow-400 fill-current"
                          : "text-gray-300"
                      }`}
                    />
                  ))}
                </div>
                <span className="text-sm text-gray-600">
                  ({product.reviewCount} reviews)
                </span>
              </div>
            </div>

            {/* Price */}
            <div className="flex items-center space-x-4">
              <span className="text-3xl font-bold text-neutral">${product.price}</span>
              {product.originalPrice && (
                <span className="text-lg text-gray-500 line-through">
                  ${product.originalPrice}
                </span>
              )}
              {product.dosage && (
                <Badge variant="outline">{product.dosage}</Badge>
              )}
            </div>

            {/* Description */}
            <div>
              <h3 className="font-semibold text-lg mb-2">Description</h3>
              <p className="text-gray-700 leading-relaxed">
                {product.description || product.shortDescription}
              </p>
            </div>

            {/* Quantity Selector and Add to Cart */}
            <div className="space-y-4">
              {isInStock && (
                <div className="flex items-center space-x-4">
                  <label className="font-medium text-gray-700">Quantity:</label>
                  <div className="flex items-center border border-gray-300 rounded-lg">
                    <Button
                      variant="ghost"
                      size="sm"
                      onClick={() => adjustQuantity(-1)}
                      disabled={quantity <= 1}
                      className="rounded-r-none"
                    >
                      <Minus className="h-4 w-4" />
                    </Button>
                    <span className="px-4 py-2 border-x min-w-[60px] text-center">
                      {quantity}
                    </span>
                    <Button
                      variant="ghost"
                      size="sm"
                      onClick={() => adjustQuantity(1)}
                      disabled={quantity >= (product.stockQuantity || 1)}
                      className="rounded-l-none"
                    >
                      <Plus className="h-4 w-4" />
                    </Button>
                  </div>
                  <span className="text-sm text-gray-600">
                    {product.stockQuantity} available
                  </span>
                </div>
              )}

              <div className="flex space-x-4">
                {product.requiresPrescription ? (
                  <Button size="lg" className="flex-1" disabled={!isInStock}>
                    <AlertCircle className="mr-2 h-5 w-5" />
                    Upload Prescription
                  </Button>
                ) : (
                  <Button
                    size="lg"
                    className="flex-1"
                    onClick={handleAddToCart}
                    disabled={!isInStock || addToCartMutation.isPending}
                  >
                    <ShoppingCart className="mr-2 h-5 w-5" />
                    {addToCartMutation.isPending ? "Adding..." : "Add to Cart"}
                  </Button>
                )}
              </div>
            </div>

            {/* Product Features */}
            <div className="grid grid-cols-3 gap-4 pt-6 border-t">
              <div className="text-center">
                <Shield className="h-8 w-8 text-primary mx-auto mb-2" />
                <p className="text-sm font-medium">FDA Approved</p>
              </div>
              <div className="text-center">
                <Truck className="h-8 w-8 text-secondary mx-auto mb-2" />
                <p className="text-sm font-medium">Free Shipping</p>
              </div>
              <div className="text-center">
                <RotateCcw className="h-8 w-8 text-purple-600 mx-auto mb-2" />
                <p className="text-sm font-medium">Easy Returns</p>
              </div>
            </div>
          </div>
        </div>

        {/* Related Products */}
        {relatedProducts && relatedProducts.length > 1 && (
          <section className="bg-white rounded-lg p-8">
            <h2 className="text-2xl font-bold text-neutral mb-6">Related Products</h2>
            <div className="grid sm:grid-cols-2 lg:grid-cols-4 gap-6">
              {relatedProducts
                .filter((p: any) => p.id !== product.id)
                .slice(0, 4)
                .map((relatedProduct: any) => (
                  <Card key={relatedProduct.id} className="group cursor-pointer hover:shadow-lg transition-shadow">
                    <CardContent className="p-4">
                      <img
                        src={relatedProduct.imageUrl || "https://images.unsplash.com/photo-1584308666744-24d5c474f2ae?auto=format&fit=crop&w=300&h=200"}
                        alt={relatedProduct.name}
                        className="w-full h-32 object-cover rounded-lg mb-3"
                      />
                      <h3 className="font-semibold text-sm mb-1 line-clamp-2">
                        {relatedProduct.name}
                      </h3>
                      <p className="text-xs text-gray-600 mb-2">
                        {relatedProduct.brand?.name}
                      </p>
                      <div className="flex items-center justify-between">
                        <span className="font-bold text-primary">
                          ${relatedProduct.price}
                        </span>
                        <Button size="sm" variant="outline">
                          View
                        </Button>
                      </div>
                    </CardContent>
                  </Card>
                ))}
            </div>
          </section>
        )}
      </div>

      <Footer />
    </div>
  );
}

client/src/pages/shop-broken.tsx.content:
import { useState, useEffect } from "react";
import { useQuery } from "@tanstack/react-query";
import { useLocation } from "wouter";
import Header from "@/components/layout/header";
import Footer from "@/components/layout/footer";
import ProductCard from "@/components/product/product-card";

import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { Badge } from "@/components/ui/badge";
import { Search, Grid3X3, List, ChevronLeft, ChevronRight } from "lucide-react";

export default function Shop() {
  const [location] = useLocation();
  const [searchQuery, setSearchQuery] = useState("");

  const [sortBy, setSortBy] = useState("popularity");
  const [viewMode, setViewMode] = useState<"grid" | "list">("grid");
  const [currentPage, setCurrentPage] = useState(1);
  const itemsPerPage = 12;



  const { data: products, isLoading: productsLoading } = useQuery({
    queryKey: ["/api/products"],
    queryFn: async () => {
      const params = new URLSearchParams();
      if (searchQuery) params.append('search', searchQuery);
      params.append('inStock', 'true');
      params.append('limit', itemsPerPage.toString());
      params.append('offset', ((currentPage - 1) * itemsPerPage).toString());

      const response = await fetch(`/api/products?${params}`);
      if (!response.ok) throw new Error('Failed to fetch products');
      return response.json();
    },
  });



  const handleSearch = (query: string) => {
    setSearchQuery(query);
    setCurrentPage(1);
  };

  const clearFilters = () => {
    setSearchQuery("");
    setCurrentPage(1);
  };



  return (
    <div className="min-h-screen bg-gray-50">
      <Header />

      {/* Page Header */}
      <section className="bg-white border-b border-gray-200 py-8">
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
          <div className="flex flex-col md:flex-row md:items-center md:justify-between">
            <div>
              <h1 className="text-3xl font-bold text-neutral">Shop Medicines & Health Products</h1>
              <p className="text-gray-600 mt-2">Find the right products for your health and wellness needs</p>
            </div>

            {/* Search Bar */}
            <div className="mt-6 md:mt-0 md:w-96">
              <div className="relative">
                <Search className="absolute left-3 top-1/2 transform -translate-y-1/2 text-gray-400 h-5 w-5" />
                <Input
                  type="text"
                  placeholder="Search for medicines, health products..."
                  value={searchQuery}
                  onChange={(e) => handleSearch(e.target.value)}
                  className="pl-10 pr-4 py-3 w-full"
                />
              </div>
            </div>
          </div>
        </div>
      </section>

      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-8">
        <div className="w-full">
            {/* Sort Options */}
            <div className="flex flex-col sm:flex-row justify-between items-center mb-8 bg-white rounded-lg p-4 shadow-sm">
              <div className="mb-4 sm:mb-0">
                <span className="text-gray-600">
                  Showing {products?.length || 0} products
                </span>
              </div>

              <div className="flex items-center space-x-4">
                <Select value={sortBy} onValueChange={setSortBy}>
                  <SelectTrigger className="w-48">
                    <SelectValue placeholder="Sort by" />
                  </SelectTrigger>
                  <SelectContent>
                    <SelectItem value="popularity">Sort by Popularity</SelectItem>
                    <SelectItem value="price-low">Price: Low to High</SelectItem>
                    <SelectItem value="price-high">Price: High to Low</SelectItem>
                    <SelectItem value="newest">Newest First</SelectItem>
                  </SelectContent>
                </Select>

                <div className="flex border border-gray-300 rounded-lg">
                  <Button
                    variant={viewMode === "grid" ? "default" : "ghost"}
                    size="sm"
                    onClick={() => setViewMode("grid")}
                    className="rounded-r-none"
                  >
                    <Grid3X3 className="h-4 w-4" />
                  </Button>
                  <Button
                    variant={viewMode === "list" ? "default" : "ghost"}
                    size="sm"
                    onClick={() => setViewMode("list")}
                    className="rounded-l-none border-l"
                  >
                    <List className="h-4 w-4" />
                  </Button>
                </div>
              </div>
            </div>

            {/* Products Grid/List */}
            {productsLoading ? (
              <div className="grid sm:grid-cols-2 lg:grid-cols-3 gap-6">
                {[...Array(12)].map((_, i) => (
                  <div key={i} className="bg-gray-200 animate-pulse rounded-lg h-96"></div>
                ))}
              </div>
            ) : products && products.length > 0 ? (
              <>
                <div className={`grid gap-6 ${
                  viewMode === "grid" 
                    ? "sm:grid-cols-2 lg:grid-cols-3" 
                    : "grid-cols-1"
                }`}>
                  {products.map((product: any) => (
                    <ProductCard 
                      key={product.id} 
                      product={product} 
                      variant={viewMode}
                    />
                  ))}
                </div>

                {/* Pagination */}
                <div className="flex justify-center items-center space-x-4 mt-12">
                  <Button
                    variant="outline"
                    onClick={() => setCurrentPage(Math.max(1, currentPage - 1))}
                    disabled={currentPage === 1}
                  >
                    <ChevronLeft className="h-4 w-4 mr-2" />
                    Previous
                  </Button>

                  <div className="flex space-x-2">
                    {[1, 2, 3].map((page) => (
                      <Button
                        key={page}
                        variant={page === currentPage ? "default" : "outline"}
                        onClick={() => setCurrentPage(page)}
                        className="w-10 h-10"
                      >
                        {page}
                      </Button>
                    ))}
                    <span className="flex items-center px-4 text-gray-600">...</span>
                    <Button
                      variant="outline"
                      onClick={() => setCurrentPage(21)}
                      className="w-10 h-10"
                    >
                      21
                    </Button>
                  </div>

                  <Button
                    variant="outline"
                    onClick={() => setCurrentPage(currentPage + 1)}
                  >
                    Next
                    <ChevronRight className="h-4 w-4 ml-2" />
                  </Button>
                </div>
              </>
            ) : (
              <div className="text-center py-12">
                <p className="text-gray-600 text-lg">No products found matching your criteria.</p>
                <Button className="mt-4" onClick={clearFilters}>
                  Clear Filters
                </Button>
              </div>
            )}
          </div>
        </div>
      </div>

      <Footer />
    </div>
  );
}

client/src/pages/shop.tsx.content:
import { useState } from "react";
import { useQuery } from "@tanstack/react-query";
import Header from "@/components/layout/header";
import Footer from "@/components/layout/footer";
import { Card, CardContent, CardFooter, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Badge } from "@/components/ui/badge";
import { Input } from "@/components/ui/input";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { Tabs, TabsContent, TabsList, TabsTrigger } from "@/components/ui/tabs";
import { ShoppingCart, Search, Filter, Star, Heart, Package } from "lucide-react";
import { useToast } from "@/hooks/use-toast";
import { apiRequest } from "@/lib/queryClient";
import { useAuth } from "@/hooks/useAuth";
import { isUnauthorizedError } from "@/lib/authUtils";
import { useProducts } from "@/contexts/ProductContext";

interface Product {
  id: string;
  name: string;
  brand: string;
  price: number;
  originalPrice?: number;
  category: string;
  imageUrl?: string;
  description?: string;
  rating?: number;
  reviewCount?: number;
}

const categories = [
  { id: "all", name: "All Products", count: 0 },
  { id: "vitamins", name: "Vitamins & Multivitamins", count: 22 },
  { id: "minerals", name: "Minerals & Trace Elements", count: 13 },
  { id: "herbal", name: "Herbal & Natural Supplements", count: 18 },
  { id: "pain-relief", name: "Pain Relief & First Aid", count: 15 },
  { id: "digestive", name: "Digestive Health", count: 12 },
  { id: "skin-care", name: "Skin & Personal Care", count: 10 },
  { id: "baby-care", name: "Baby & Child Care", count: 8 },
  { id: "medical-devices", name: "Medical Devices", count: 5 }
];

export default function Shop() {
  const [sortBy, setSortBy] = useState("name");
  const [priceRange, setPriceRange] = useState("all");
  const { isAuthenticated } = useAuth();
  const { toast } = useToast();

  // Use ProductContext for shared state management
  const {
    products,
    categories,
    isLoading,
    searchQuery,
    setSearchQuery,
    selectedCategory,
    setSelectedCategory,
    filteredProducts
  } = useProducts();

  // Convert categories from context to match the existing format
  const categoryOptions = [
    { id: "all", name: "All Products", count: products.length },
    ...categories.map(category => ({
      id: category.toLowerCase().replace(/\s+/g, '-'),
      name: category,
      count: products.filter(p => (p.category?.name || p.Category) === category).length
    }))
  ];

  const addToCart = async (productId: string) => {
    if (!isAuthenticated) {
      toast({
        title: "Login Required",
        description: "Please login to add items to your cart.",
        variant: "destructive",
      });
      setTimeout(() => {
        window.location.href = "/api/login";
      }, 1000);
      return;
    }

    try {
      await apiRequest("POST", "/api/cart", { productId, quantity: 1 });
      toast({
        title: "Added to Cart",
        description: "Item has been added to your cart successfully.",
      });
    } catch (error) {
      if (isUnauthorizedError(error as Error)) {
        toast({
          title: "Session Expired",
          description: "Please login again to continue.",
          variant: "destructive",
        });
        setTimeout(() => {
          window.location.href = "/api/login";
        }, 1000);
        return;
      }
      toast({
        title: "Error",
        description: "Failed to add item to cart. Please try again.",
        variant: "destructive",
      });
    }
  };

  // Additional filtering for price range since ProductContext handles search and category
  const priceFilteredProducts = filteredProducts.filter((product) => {
    const price = parseFloat(product.price || product.Price || '0');
    const matchesPrice = priceRange === "all" ||
                        (priceRange === "under-100" && price < 100) ||
                        (priceRange === "100-300" && price >= 100 && price <= 300) ||
                        (priceRange === "over-300" && price > 300);

    return matchesPrice;
  });

  const sortedProducts = [...priceFilteredProducts].sort((a, b) => {
    const priceA = parseFloat(a.price || a.Price || '0');
    const priceB = parseFloat(b.price || b.Price || '0');
    const nameA = a.name || a['Product Name'] || '';
    const nameB = b.name || b['Product Name'] || '';
    const ratingA = parseFloat(a.rating || '0');
    const ratingB = parseFloat(b.rating || '0');
    switch (sortBy) {
      case "price-low":
        return priceA - priceB;
      case "price-high":
        return priceB - priceA;
      case "rating":
        return ratingB - ratingA;
      case "name":
      default:
        return nameA.localeCompare(nameB);
    }
  });

  return (
    <div className="min-h-screen bg-gray-50">
      <Header />
      <div className="container mx-auto px-4 py-8">
        {/* Header */}
        <div className="mb-8">
          <h1 className="text-4xl font-bold text-primary mb-2">Shop Our Products</h1>
          <p className="text-gray-600 text-lg">
            Discover quality pharmaceutical products and health supplements for your wellness journey
          </p>
        </div>

        {/* Search and Filters */}
        <div className="bg-white rounded-lg shadow-sm border p-6 mb-8">
          <div className="grid grid-cols-1 md:grid-cols-4 gap-4">
            <div className="relative">
              <Search className="absolute left-3 top-1/2 transform -translate-y-1/2 text-gray-400 h-4 w-4" />
              <Input
                placeholder="Search products..."
                value={searchQuery}
                onChange={(e) => setSearchQuery(e.target.value)}
                className="pl-10"
              />
            </div>

            <Select value={sortBy} onValueChange={setSortBy}>
              <SelectTrigger>
                <SelectValue placeholder="Sort by" />
              </SelectTrigger>
              <SelectContent>
                <SelectItem value="name">Name A-Z</SelectItem>
                <SelectItem value="price-low">Price: Low to High</SelectItem>
                <SelectItem value="price-high">Price: High to Low</SelectItem>
                <SelectItem value="rating">Highest Rated</SelectItem>
              </SelectContent>
            </Select>

            <Select value={priceRange} onValueChange={setPriceRange}>
              <SelectTrigger>
                <SelectValue placeholder="Price range" />
              </SelectTrigger>
              <SelectContent>
                <SelectItem value="all">All Prices</SelectItem>
                <SelectItem value="under-100">Under ₵100</SelectItem>
                <SelectItem value="100-300">₵100 - ₵300</SelectItem>
                <SelectItem value="over-300">Over ₵300</SelectItem>
              </SelectContent>
            </Select>

            <Button variant="outline" className="w-full">
              <Filter className="mr-2 h-4 w-4" />
              More Filters
            </Button>
          </div>
        </div>

        <div className="flex flex-col lg:flex-row gap-8">
          {/* Categories Sidebar */}
          <div className="lg:w-1/4">
            <Card>
              <CardHeader>
                <CardTitle className="flex items-center">
                  <Package className="mr-2 h-5 w-5" />
                  Categories
                </CardTitle>
              </CardHeader>
              <CardContent>
                <div className="space-y-2">
                  {categoryOptions.map((category) => (
                    <button
                      key={category.id}
                      onClick={() => setSelectedCategory(category.name === 'All Products' ? 'All' : category.name)}
                      className={`w-full text-left p-3 rounded-lg transition-colors ${
                        (selectedCategory === 'All' && category.name === 'All Products') || 
                        (selectedCategory === category.name)
                          ? "bg-primary text-white"
                          : "hover:bg-gray-100"
                      }`}
                    >
                      <div className="flex justify-between items-center">
                        <span className="text-sm font-medium">{category.name}</span>
                        <Badge variant="secondary" className="text-xs">
                          {category.count}
                        </Badge>
                      </div>
                    </button>
                  ))}
                </div>
              </CardContent>
            </Card>
          </div>

          {/* Products Grid */}
          <div className="lg:w-3/4">
            {isLoading ? (
              <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
                {[...Array(9)].map((_, i) => (
                  <Card key={i} className="animate-pulse">
                    <div className="h-48 bg-gray-200 rounded-t-lg"></div>
                    <CardContent className="p-4">
                      <div className="h-4 bg-gray-200 rounded mb-2"></div>
                      <div className="h-3 bg-gray-200 rounded mb-2"></div>
                      <div className="h-4 bg-gray-200 rounded w-1/2"></div>
                    </CardContent>
                  </Card>
                ))}
              </div>
            ) : (
              <>
                <div className="flex justify-between items-center mb-6">
                  <p className="text-gray-600">
                    Showing {sortedProducts.length} of {products.length} products
                  </p>
                </div>

                <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
                  {sortedProducts.map((product) => (
                    <Card key={product.id || product.name || product['Product Name']} className="group hover:shadow-lg transition-shadow">
                      <div className="relative overflow-hidden rounded-t-lg">
                        <img
                          src={product.imageUrl || product.ImageURL || "https://images.unsplash.com/photo-1584308666744-24d5c474f2ae?auto=format&fit=crop&w=400&h=300"}
                          alt={product.name || product['Product Name']}
                          className="w-full h-48 object-cover group-hover:scale-105 transition-transform duration-300"
                        />
                        <div className="absolute top-2 right-2 flex gap-2">
                          {product.originalPrice && product.originalPrice > product.price && (
                            <Badge className="bg-red-500 text-white">
                              Save ₵{(product.originalPrice - product.price).toFixed(2)}
                            </Badge>
                          )}
                        </div>
                        <Button
                          size="sm"
                          variant="ghost"
                          className="absolute top-2 left-2 text-white hover:text-red-500"
                        >
                          <Heart className="h-4 w-4" />
                        </Button>
                      </div>

                      <CardContent className="p-4">
                        <div className="mb-2">
                          <Badge variant="outline" className="text-xs mb-2">
                            {product.brand}
                          </Badge>
                        </div>

                        <h3 className="font-semibold text-sm line-clamp-2 mb-2 group-hover:text-primary transition-colors">
                          {product.name}
                        </h3>

                        {product.rating && (
                          <div className="flex items-center mb-2">
                            <div className="flex items-center">
                              {[...Array(5)].map((_, i) => (
                                <Star
                                  key={i}
                                  className={`h-3 w-3 ${
                                    i < Math.floor(product.rating!)
                                      ? "text-yellow-400 fill-current"
                                      : "text-gray-300"
                                  }`}
                                />
                              ))}
                            </div>
                            <span className="text-xs text-gray-600 ml-1">
                              ({product.reviewCount || 0})
                            </span>
                          </div>
                        )}

                        <div className="flex items-center justify-between mb-3">
                          <div className="flex items-center gap-2">
                            <span className="text-lg font-bold text-primary">
                              ₵{product.price.toFixed(2)}
                            </span>
                            {product.originalPrice && product.originalPrice > product.price && (
                              <span className="text-sm text-gray-500 line-through">
                                ₵{product.originalPrice.toFixed(2)}
                              </span>
                            )}
                          </div>
                        </div>
                      </CardContent>

                      <CardFooter className="p-4 pt-0">
                        <Button
                          onClick={() => addToCart(product.id)}
                          className="w-full"
                        >
                          <ShoppingCart className="mr-2 h-4 w-4" />
                          Add to Cart
                        </Button>
                      </CardFooter>
                    </Card>
                  ))}
                </div>
              </>
            )}
          </div>
        </div>
      </div>
      <Footer />
    </div>
  );
}

client/src/components/auth/AdminRoute.ts.content:
import { useEffect } from "react";
import { useLocation } from "wouter";

interface AdminRouteProps {
  children: React.ReactNode;
}

export function AdminRoute({ children }: AdminRouteProps) {
  const [, setLocation] = useLocation();

  useEffect(() => {
    const userRole = localStorage.getItem("role");

    if (userRole !== "admin") {
      setLocation("/admin/login");
    }
  }, [setLocation]);

  const userRole = localStorage.getItem("role");

  if (userRole !== "admin") {
    return null;
  }

  return <>{children}</>;
}

