import { useState, useEffect } from "react";
import { useQuery } from "@tanstack/react-query";
import { useAuth } from "@/hooks/useAuth";
import { Button } from "@/components/ui/button";
import { Badge } from "@/components/ui/badge";
import { DropdownMenu, DropdownMenuContent, DropdownMenuItem, DropdownMenuLabel, DropdownMenuSeparator, DropdownMenuTrigger } from "@/components/ui/dropdown-menu";
import { Avatar, AvatarFallback, AvatarImage } from "@/components/ui/avatar";
import CartSidebar from "@/components/cart/cart-sidebar";
import { CartBadge } from "@/components/cart/CartBadge";
import { Link, useLocation } from "wouter";
import { 
  ShoppingCart, 
  User, 
  Phone, 
  Mail,
  Menu,
  X,
  Settings,
  Shield,
  LogOut,
  UserCircle,
  ChevronDown
} from "lucide-react";

export default function Header() {
  const { isAuthenticated, user } = useAuth();
  const [location, setLocation] = useLocation();
  const [isCartOpen, setIsCartOpen] = useState(false);
  const [isMobileMenuOpen, setIsMobileMenuOpen] = useState(false);
  const [currentUser, setCurrentUser] = useState<any>(null);
  const [userRole, setUserRole] = useState(localStorage.getItem('role'));
  const [isUserAuthenticated, setIsUserAuthenticated] = useState(() => {
    const currentRole = localStorage.getItem('role');
    return currentRole === 'admin';
  });

  const { data: cartItems } = useQuery({
    queryKey: ["/api/cart"],
    enabled: isUserAuthenticated,
  });

  const cartItemCount = Array.isArray(cartItems) ? cartItems.length : 0;

  // Check user role and authentication
  useEffect(() => {
    const currentRole = localStorage.getItem('role');
    setUserRole(currentRole);

    if (isAuthenticated && user) {
      // Set role for regular authenticated users
      if (!currentRole) {
        localStorage.setItem('role', 'user');
        setUserRole('user');
      }
      setCurrentUser({
        ...user,
        isAdmin: currentRole === 'admin'
      });
      setIsUserAuthenticated(true);
    } else if (currentRole === 'admin') {
      // Hardcoded admin from localStorage
      const adminUsername = localStorage.getItem('adminUsername');
      const userDisplayName = localStorage.getItem('userDisplayName');

      if (adminUsername === 'Admin1') {
        setCurrentUser({
          id: 'admin-hardcoded-001',
          username: adminUsername,
          email: 'admin@smilepills.com',
          isAdmin: true,
          adminRole: 'super_admin',
          firstName: 'Admin',
          lastName: '1',
          displayName: userDisplayName || 'Admin 1'
        });
        setIsUserAuthenticated(true);
      }
    } else {
      setCurrentUser(null);
      setIsUserAuthenticated(false);
    }
  }, [isAuthenticated, user]);

  const handleLogout = async () => {
    try {
      // Clear all localStorage session data
      localStorage.removeItem('role');
      localStorage.removeItem('isAdmin');
      localStorage.removeItem('adminUsername');
      localStorage.removeItem('userDisplayName');

      // Update userRole state immediately
      setUserRole(null);
      setCurrentUser(null);
      setIsUserAuthenticated(false);

      // Try backend logout
      await fetch('/api/auth/logout', { method: 'POST', credentials: 'include' });

      window.location.href = '/';
    } catch (error) {
      console.error('Logout error:', error);
      // Still clear localStorage and redirect
      localStorage.removeItem('role');
      localStorage.removeItem('isAdmin');
      localStorage.removeItem('adminUsername');
      localStorage.removeItem('userDisplayName');
      setUserRole(null);
      setCurrentUser(null);
      setIsUserAuthenticated(false);
      window.location.href = '/';
    }
  };

  const getUserInitials = (user: any) => {
    if (user?.firstName && user?.lastName) {
      return `${user.firstName[0]}${user.lastName[0]}`;
    }
    if (user?.username) {
      return user.username.substring(0, 2).toUpperCase();
    }
    if (user?.email) {
      return user.email.substring(0, 2).toUpperCase();
    }
    return 'U';
  };

  const getUserDisplayName = (user: any) => {
    if (user?.firstName && user?.lastName) {
      return `${user.firstName} ${user.lastName}`;
    }
    if (user?.username) {
      return user.username;
    }
    if (user?.email) {
      return user.email;
    }
    return 'User';
  };


  const categories = [
    { name: "Home", slug: "home", href: "/" },
    { name: "Shop", slug: "shop", href: "/shop" },
    { name: "About", slug: "about", href: "/about" },
  ];

  return (
    <>
