                          {currentUser?.isAdmin && (
                            <Badge variant="secondary" className="mt-1 w-fit">
                              {currentUser?.adminRole || 'Admin'}
                            </Badge>
                          )}
                        </div>
                      </DropdownMenuLabel>
                      <DropdownMenuSeparator />

                      <DropdownMenuItem 
                        onClick={() => {
                          const userRole = localStorage.getItem('role');
                          if (userRole === 'admin') {
                            setLocation('/admin');
                          } else if (userRole === 'user') {
                            setLocation('/account');
                          } else {
                            setLocation('/login');
                          }
                        }}
                        data-testid="dashboard-link"
                      >
                        {currentUser?.isAdmin ? (
                          <>
                            <Shield className="mr-2 h-4 w-4" />
                            <span>Admin Dashboard</span>
                          </>
                        ) : (
                          <>
                            <UserCircle className="mr-2 h-4 w-4" />
                            <span>My Dashboard</span>
                          </>
                        )}
                      </DropdownMenuItem>



                      {!currentUser?.isAdmin && (
                        <>
                          <DropdownMenuItem 
                            onClick={() => setLocation('/prescription')}
                            data-testid="prescription-link"
                          >
                            <UserCircle className="mr-2 h-4 w-4" />
                            <span>My Prescriptions</span>
                          </DropdownMenuItem>

                          <DropdownMenuItem 
                            onClick={() => setLocation('/cart')}
                            data-testid="my-orders-link"
                          >
                            <ShoppingCart className="mr-2 h-4 w-4" />
                            <span>My Orders</span>
                          </DropdownMenuItem>
                        </>
                      )}

                      <DropdownMenuSeparator />

                      <DropdownMenuItem 
                        onClick={handleLogout}
                        data-testid="logout-button"
                      >
                        <LogOut className="mr-2 h-4 w-4" />
                        <span>Log out</span>
                      </DropdownMenuItem>
                    </DropdownMenuContent>
                  </DropdownMenu>
                ) : null}
              </div>
            </div>
          </div>
        </div>

        {/* Main navigation */}
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
          <nav className="flex items-center justify-between py-2">
            {/* Logo - Always leads to Landing page */}
            <Link href="/">
              <div className="flex items-center space-x-3 cursor-pointer" data-testid="logo-home-link">
                <img 
                  src="/assets/IMG_1598%20(1)_1754986183203.PNG" 
                  alt="Smile Pills Ltd - Smile Forever" 
                  className="h-10 w-auto object-contain"
                />
              </div>
            </Link>

            {/* Spacer */}
            <div className="flex-1"></div>

            {/* Cart and User actions */}
            <div className="flex items-center space-x-4">
              {/* Mobile menu button */}
              <Button
                variant="ghost"
                size="sm"
                className="md:hidden"
                onClick={() => setIsMobileMenuOpen(!isMobileMenuOpen)}
              >
                {isMobileMenuOpen ? <X className="h-5 w-5" /> : <Menu className="h-5 w-5" />}
              </Button>

              {/* Cart */}
              <Link href="/cart">
                <Button
                  variant="ghost"
                  size="sm"
                  className="relative"
                  data-testid="button-cart-header"
                >
                  <ShoppingCart className="h-5 w-5" />
                  <CartBadge />
                </Button>
              </Link>

              {/* Login/Logout button */}
              {!isUserAuthenticated ? (
                <Link href="/login">
                  <Button 
                    variant="ghost"
                    size="sm"
                    className="flex items-center"
                    data-testid="login-main"
                  >
                    <User className="h-4 w-4 mr-2" />
                    Login
                  </Button>
                </Link>
              ) : (
                <Button 
                  variant="ghost"
                  size="sm"
                  onClick={handleLogout}
                  className="flex items-center"
                  data-testid="logout-main"
                >
                  <LogOut className="h-4 w-4 mr-2" />
                  Logout
                </Button>
              )}

            </div>
          </nav>



          {/* Category navigation */}
          <div className="hidden md:block pb-2">
            <nav className="flex justify-center space-x-8">
              {categories.map((category) => (
                <Link key={category.slug} href={category.href}>
                  <span
                    className={`whitespace-nowrap pb-2 transition-colors font-medium ${
                      location === category.href || 
                      (category.slug && location.includes(`category=${category.slug}`))
                        ? "text-primary border-b-2 border-primary"
                        : "text-gray-600 hover:text-primary"
                    }`}
                  >
                    {category.name}
                  </span>
                </Link>
              ))}
            </nav>
          </div>

          {/* Mobile category navigation */}
          {isMobileMenuOpen && (
            <div className="md:hidden pb-4 border-t pt-4">
              <nav className="grid grid-cols-1 gap-2">
                {categories.map((category) => (
                  <Link key={category.slug} href={category.href}>
                    <Button
                      variant="ghost"
                      className="w-full justify-start text-left"
                      onClick={() => setIsMobileMenuOpen(false)}
                    >
                      {category.name}
                    </Button>
                  </Link>
                ))}
              </nav>
            </div>
          )}
        </div>
      </header>

      {/* Cart Sidebar */}
      <CartSidebar 
        isOpen={isCartOpen} 
        onClose={() => setIsCartOpen(false)} 
      />
    </>
  );
}

client/src/components/product/product-ca.content:
import { useState } from "react";
import { useMutation, useQueryClient } from "@tanstack/react-query";
import { useAuth } from "@/hooks/useAuth";
import { Card, CardContent } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Badge } from "@/components/ui/badge";
import { useToast } from "@/hooks/use-toast";
import { apiRequest } from "@/lib/queryClient";
import { isUnauthorizedError } from "@/lib/authUtils";
import { formatPrice } from "@/lib/currency";
import { Link } from "wouter";
import { 
  ShoppingCart, 
  Heart, 
  Star, 
  AlertCircle,
  CheckCircle
} from "lucide-react";

interface ProductCardProps {
  product: {
    id: string;
    name: string;
    slug: string;
    price: string;
    originalPrice?: string;
    dosage?: string;
    imageUrl?: string;
    stockQuantity: number;
    requiresPrescription: boolean;
    rating?: string;
    reviewCount: number;
    brand?: {
      name: string;
    };
    category?: {
      name: string;
    };
  };
  variant?: "grid" | "list";
}

export default function ProductCard({ product, variant = "grid" }: ProductCardProps) {
  const { isAuthenticated } = useAuth();
  const { toast } = useToast();
  const queryClient = useQueryClient();
  const [isHovered, setIsHovered] = useState(false);

  const addToCartMutation = useMutation({
    mutationFn: async () => {
      const response = await apiRequest("POST", "/api/cart", {
        productId: product.id,
        quantity: 1,
      });
      return response.json();
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/cart"] });
      toast({
        title: "Added to Cart",
        description: `${product.name} has been added to your cart.`,
      });
    },
    onError: (error) => {
      if (isUnauthorizedError(error)) {
        toast({
          title: "Please Login",
          description: "You need to be logged in to add items to cart.",
          variant: "destructive",
        });
        setTimeout(() => {
          window.location.href = "/api/login";
        }, 1000);
        return;
      }
      toast({
        title: "Error",
        description: "Failed to add item to cart. Please try again.",
        variant: "destructive",
      });
    },
  });

  const handleAddToCart = (e: React.MouseEvent) => {
    e.preventDefault();
    e.stopPropagation();

    if (!isAuthenticated) {
      toast({
        title: "Please Login",
        description: "You need to be logged in to add items to cart.",
        variant: "destructive",
      });
      setTimeout(() => {
        window.location.href = "/api/login";
      }, 1000);
      return;
    }

    addToCartMutation.mutate();
  };

  const isInStock = product.stockQuantity > 0;
  const rating = parseFloat(product.rating || "0");

  if (variant === "list") {
    return (
      <Link href={`/product/${product.slug}`}>
        <Card className="group cursor-pointer hover:shadow-lg transition-shadow duration-300 overflow-hidden">
          <CardContent className="p-6">
            <div className="flex items-center space-x-6">
              <div className="relative flex-shrink-0">
                <img
                  src={product.imageUrl || "https://images.unsplash.com/photo-1584308666744-24d5c474f2ae?auto=format&fit=crop&w=200&h=200"}
                  alt={product.name}
                  className="w-24 h-24 object-cover rounded-lg group-hover:scale-105 transition-transform duration-300"
                />
                {!isInStock && (
                  <div className="absolute inset-0 bg-black/50 rounded-lg flex items-center justify-center">
                    <Badge variant="destructive" className="text-xs">
                      Out of Stock
                    </Badge>
                  </div>
                )}
              </div>

              <div className="flex-1 min-w-0">
                <div className="flex items-center justify-between mb-2">
                  <div className="flex items-center space-x-2">
                    <Badge 
                      variant={product.requiresPrescription ? "destructive" : "secondary"}
                      className="text-xs"
                    >
                      {product.requiresPrescription ? "Rx" : "OTC"}
                    </Badge>
                    {isInStock ? (
                      <Badge variant="default" className="bg-secondary text-white text-xs">
                        <CheckCircle className="h-3 w-3 mr-1" />
                        In Stock
                      </Badge>
                    ) : (
                      <Badge variant="destructive" className="text-xs">
                        Out of Stock
                      </Badge>
                    )}
                  </div>

                  <div className="flex text-yellow-400">
                    {[...Array(5)].map((_, i) => (
                      <Star
                        key={i}
                        className={`h-3 w-3 ${
                          i < Math.floor(rating)
                            ? "text-yellow-400 fill-current"
                            : "text-gray-300"
                        }`}
                      />
                    ))}
                    <span className="text-gray-600 text-xs ml-1">({product.reviewCount})</span>
                  </div>
                </div>

                <h3 className="font-semibold text-lg mb-1 text-neutral line-clamp-1">
                  {product.name}
                </h3>
                <p className="text-gray-600 text-sm mb-2">{product.brand?.name}</p>

                <div className="flex items-center justify-between">
                  <div className="flex items-center space-x-2">
                    <span className="text-xl font-bold text-neutral">{formatPrice(product.price)}</span>
                    {product.originalPrice && (
                      <span className="text-sm text-gray-500 line-through">
                        {formatPrice(product.originalPrice)}
                      </span>
                    )}
                    {product.dosage && (
                      <Badge variant="outline" className="text-xs">
                        {product.dosage}
                      </Badge>
                    )}
                  </div>

                  {product.requiresPrescription ? (
                    <Button size="sm" variant="outline" disabled={!isInStock} onClick={handleAddToCart}>
                      <AlertCircle className="h-4 w-4 mr-2" />
                      Upload Rx
                    </Button>
                  ) : (
                    <Button
                      size="sm"
                      onClick={handleAddToCart}
                      disabled={!isInStock || addToCartMutation.isPending}
                    >
                      <ShoppingCart className="h-4 w-4 mr-2" />
                      {addToCartMutation.isPending ? "Adding..." : "Add to Cart"}
                    </Button>
                  )}
                </div>
              </div>
            </div>
          </CardContent>
        </Card>
      </Link>
    );
  }

  return (
    <Link href={`/product/${product.slug}`}>
      <Card 
        className="group cursor-pointer hover:shadow-lg transition-shadow duration-300 overflow-hidden h-full"
        onMouseEnter={() => setIsHovered(true)}
        onMouseLeave={() => setIsHovered(false)}
      >
        <div className="relative">
          <img
            src={product.imageUrl || "https://images.unsplash.com/photo-1584308666744-24d5c474f2ae?auto=format&fit=crop&w=400&h=200"}
            alt={product.name}
            className="w-full h-32 object-cover group-hover:scale-105 transition-transform duration-300"
          />

          {/* Stock status badge */}
          <div className="absolute top-3 right-3">
            {!isInStock && (
              <Badge variant="destructive" className="text-xs">
                Out of Stock
              </Badge>
            )}
          </div>

          {/* Wishlist button */}


          {!isInStock && (
            <div className="absolute inset-0 bg-black/50 flex items-center justify-center">
              <Badge variant="destructive" className="text-lg px-4 py-2">
                Out of Stock
              </Badge>
            </div>
          )}
        </div>

        <CardContent className="p-4 flex flex-col">
          <div className="flex items-center justify-between mb-2">
            <Badge 
              variant={product.requiresPrescription ? "destructive" : "secondary"}
              className="text-xs"
            >
              {product.requiresPrescription ? "Rx" : "OTC"}
            </Badge>
            {product.dosage && (
              <Badge variant="outline" className="text-xs">
                {product.dosage}
              </Badge>
            )}
          </div>

          <h3 className="font-semibold text-sm mb-1 text-neutral line-clamp-2">
            {product.name}
          </h3>
          <p className="text-gray-500 text-xs mb-2">{product.brand?.name}</p>

          <div className="flex items-center justify-between mb-3">
            <div className="flex items-center space-x-1">
              <span className="text-lg font-bold text-primary">{formatPrice(product.price)}</span>
              {product.originalPrice && (
                <span className="text-xs text-gray-400 line-through">
                  {formatPrice(product.originalPrice)}
                </span>
              )}
            </div>
            <div className="flex text-yellow-400">
              {[...Array(5)].map((_, i) => (
                <Star
                  key={i}
                  className={`h-3 w-3 ${
                    i < Math.floor(rating)
                      ? "text-yellow-400 fill-current"
                      : "text-gray-300"
                  }`}
                />
              ))}
              <span className="text-gray-500 text-xs ml-1">({product.reviewCount})</span>
            </div>
          </div>

          {product.requiresPrescription ? (
            <Button
              size="sm"
              className="w-full bg-accent text-white hover:bg-red-700 text-xs"
              onClick={handleAddToCart}
              disabled={!isInStock}
            >
              <AlertCircle className="mr-1 h-3 w-3" />
              Upload Rx
            </Button>
          ) : (
            <Button
              size="sm"
              className="w-full text-xs"
              onClick={handleAddToCart}
              disabled={!isInStock || addToCartMutation.isPending}
            >
              <ShoppingCart className="mr-1 h-3 w-3" />
              {addToCartMutation.isPending ? "Adding..." : "Add to Cart"}
            </Button>
          )}
        </CardContent>
      </Card>
    </Link>
  );
}

client/src/components/product/product-fi.content:
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Checkbox } from "@/components/ui/checkbox";
import { Badge } from "@/components/ui/badge";
import { Filter, X } from "lucide-react";

interface ProductFiltersProps {
  categories: Array<{ id: string; name: string; slug: string }>;
  brands: Array<{ id: string; name: string }>;
  selectedCategory: string;
  selectedBrand: string;
  selectedPrice: string;
  onFilterChange: (filterType: string, value: string) => void;
  onClearFilters: () => void;
}

export default function ProductFilters({
  categories,
  brands,
  selectedCategory,
  selectedBrand,
  selectedPrice,
  onFilterChange,
  onClearFilters,
}: ProductFiltersProps) {
  const priceRanges = [
    { value: "0-10", label: "Under $10" },
    { value: "10-50", label: "$10 - $50" },
    { value: "50-100", label: "$50 - $100" },
    { value: "100+", label: "Over $100" },
  ];

  const activeFiltersCount = [selectedCategory, selectedBrand, selectedPrice].filter(Boolean).length;

  return (
    <Card className="bg-white shadow-sm sticky top-24">
      <CardHeader>
        <CardTitle className="flex items-center justify-between">
          <div className="flex items-center">
            <Filter className="h-5 w-5 mr-2 text-primary" />
            Filters
          </div>
          {activeFiltersCount > 0 && (
            <div className="flex items-center space-x-2">
              <Badge variant="secondary" className="text-xs">
                {activeFiltersCount}
              </Badge>
              <Button 
                variant="ghost" 
                size="sm" 
                onClick={onClearFilters}
                className="text-xs p-1 h-auto"
              >
                <X className="h-3 w-3" />
              </Button>
            </div>
          )}
        </CardTitle>
      </CardHeader>

      <CardContent className="space-y-6">
        {/* Price Range */}
        <div>
          <h3 className="font-medium text-sm mb-3">Price Range</h3>
          <div className="space-y-2">
            {priceRanges.map((range) => (
              <div key={range.value} className="flex items-center space-x-2">
                <Checkbox
                  id={`price-${range.value}`}
                  checked={selectedPrice === range.value}
                  onCheckedChange={(checked) => {
                    onFilterChange('price', checked ? range.value : '');
                  }}
                />
                <label 
                  htmlFor={`price-${range.value}`} 
                  className="text-sm cursor-pointer hover:text-primary transition-colors"
                >
                  {range.label}
                </label>
              </div>
            ))}
          </div>
        </div>

        {/* Categories */}
        <div>
          <h3 className="font-medium text-sm mb-3">Category</h3>
          <div className="space-y-2">
            {categories.map((category) => (
              <div key={category.id} className="flex items-center space-x-2">
                <Checkbox
                  id={`category-${category.id}`}
                  checked={selectedCategory === category.slug}
                  onCheckedChange={(checked) => {
                    onFilterChange('category', checked ? category.slug : '');
                  }}
                />
                <label 
                  htmlFor={`category-${category.id}`} 
                  className="text-sm cursor-pointer hover:text-primary transition-colors"
                >
                  {category.name}
                </label>
              </div>
            ))}
          </div>
        </div>

        {/* Brands */}
        <div>
          <h3 className="font-medium text-sm mb-3">Brand</h3>
          <div className="space-y-2 max-h-48 overflow-y-auto custom-scrollbar">
            {brands.map((brand) => (
              <div key={brand.id} className="flex items-center space-x-2">
                <Checkbox
                  id={`brand-${brand.id}`}
                  checked={selectedBrand === brand.id}
                  onCheckedChange={(checked) => {
                    onFilterChange('brand', checked ? brand.id : '');
                  }}
                />
                <label 
                  htmlFor={`brand-${brand.id}`} 
                  className="text-sm cursor-pointer hover:text-primary transition-colors"
                >
                  {brand.name}
                </label>
              </div>
            ))}
          </div>
        </div>

        {/* Availability */}
        <div>
          <h3 className="font-medium text-sm mb-3">Availability</h3>
          <div className="space-y-2">
            <div className="flex items-center space-x-2">
              <Checkbox id="in-stock" defaultChecked />
              <label htmlFor="in-stock" className="text-sm cursor-pointer hover:text-primary transition-colors">
                In Stock
              </label>
            </div>
            <div className="flex items-center space-x-2">
              <Checkbox id="out-of-stock" />
              <label htmlFor="out-of-stock" className="text-sm cursor-pointer hover:text-primary transition-colors">
                Out of Stock
              </label>
            </div>
          </div>
        </div>

        {/* Clear Filters Button */}
        {activeFiltersCount > 0 && (
          <Button 
            variant="outline" 
            className="w-full" 
            onClick={onClearFilters}
          >
            Clear All Filters
          </Button>
        )}
      </CardContent>
    </Card>
  );
}

